INSERT INTO "Quran"(id,imlai) VALUES(4968,'فَبِأَيِّ آَلَاءِ رَبِّكُمَا تُكَذِّبَانِ');
INSERT INTO "Quran"(id,imlai) VALUES(4969,'فِيهِمَا فَاكِهَةٌ وَنَخْلٌ وَرُمَّانٌ');
