INSERT INTO "Quran"(id,imlai) VALUES(4975,'لَمْ يَطْمِثْهُنَّ إِنْسٌ قَبْلَهُمْ وَلَا جَانٌّ');
INSERT INTO "Quran"(id,imlai) VALUES(4976,'فَبِأَيِّ آَلَاءِ رَبِّكُمَا تُكَذِّبَانِ');
INSERT INTO "Quran"(id,imlai) VALUES(4977,'مُتَّكِئِينَ عَلَى رَفْرَفٍ خُضْرٍ وَعَبْقَرِيٍّ حِسَانٍ');
INSERT INTO "Quran"(id,imlai) VALUES(4978,'فَبِأَيِّ آَلَاءِ رَبِّكُمَا تُكَذِّبَانِ');
INSERT INTO "Quran"(id,imlai) VALUES(4979,'تَبَارَكَ اسْمُ رَبِّكَ ذِي الْجَلَالِ وَالْإِكْرَامِ');
INSERT INTO "Quran"(id,imlai) VALUES(4980,'إِذَا وَقَعَتِ الْوَاقِعَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(4981,'لَيْسَ لِوَقْعَتِهَا كَاذِبَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(4982,'خَافِضَةٌ رَافِعَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(4983,'إِذَا رُجَّتِ الْأَرْضُ رَجّاً');
INSERT INTO "Quran"(id,imlai) VALUES(4984,'وَبُسَّتِ الْجِبَالُ بَسّاً');
INSERT INTO "Quran"(id,imlai) VALUES(4985,'فَكَانَتْ هَبَاءً مُنْبَثّاً');
INSERT INTO "Quran"(id,imlai) VALUES(4986,'وَكُنْتُمْ أَزْوَاجاً ثَلَاثَةً');
INSERT INTO "Quran"(id,imlai) VALUES(4987,'فَأَصْحَابُ الْمَيْمَنَةِ مَا أَصْحَابُ الْمَيْمَنَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(4988,'وَأَصْحَابُ الْمَشْأَمَةِ مَا أَصْحَابُ الْمَشْأَمَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(4989,'وَالسَّابِقُونَ السَّابِقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(4990,'أُولَئِكَ الْمُقَرَّبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(4991,'فِي جَنَّاتِ النَّعِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(4992,'ثُلَّةٌ مِنَ الْأَوَّلِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(4993,'وَقَلِيلٌ مِنَ الْآَخِرِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(4994,'عَلَى سُرُرٍ مَوْضُونَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(4995,'مُتَّكِئِينَ عَلَيْهَا مُتَقَابِلِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(4996,'يَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُخَلَّدُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(4997,'بِأَكْوَابٍ وَأَبَارِيقَ وَكَأْسٍ مِنْ مَعِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(4998,'لَا يُصَدَّعُونَ عَنْهَا وَلَا يُنْزِفُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(4999,'وَفَاكِهَةٍ مِمَّا يَتَخَيَّرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5000,'وَلَحْمِ طَيْرٍ مِمَّا يَشْتَهُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5001,'وَحُورٌ عِينٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5002,'كَأَمْثَالِ اللُّؤْلُؤِ الْمَكْنُونِ');
INSERT INTO "Quran"(id,imlai) VALUES(5003,'جَزَاءً بِمَا كَانُوا يَعْمَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5004,'لَا يَسْمَعُونَ فِيهَا لَغْواً وَلَا تَأْثِيماً');
INSERT INTO "Quran"(id,imlai) VALUES(5005,'إِلَّا قِيلاً سَلَاماً سَلَاماً');
INSERT INTO "Quran"(id,imlai) VALUES(5006,'وَأَصْحَابُ الْيَمِينِ مَا أَصْحَابُ الْيَمِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5007,'فِي سِدْرٍ مَخْضُودٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5008,'وَطَلْحٍ مَنْضُودٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5009,'وَظِلٍّ مَمْدُودٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5010,'وَمَاءٍ مَسْكُوبٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5011,'وَفَاكِهَةٍ كَثِيرَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5012,'لَا مَقْطُوعَةٍ وَلَا مَمْنُوعَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5013,'وَفُرُشٍ مَرْفُوعَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5014,'إِنَّا أَنْشَأْنَاهُنَّ إِنْشَاءً');
INSERT INTO "Quran"(id,imlai) VALUES(5015,'فَجَعَلْنَاهُنَّ أَبْكَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5016,'عُرُباً أَتْرَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5017,'لِأَصْحَابِ الْيَمِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5018,'ثُلَّةٌ مِنَ الْأَوَّلِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5019,'وَثُلَّةٌ مِنَ الْآَخِرِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5020,'وَأَصْحَابُ الشِّمَالِ مَا أَصْحَابُ الشِّمَالِ');
INSERT INTO "Quran"(id,imlai) VALUES(5021,'فِي سَمُومٍ وَحَمِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5022,'وَظِلٍّ مِنْ يَحْمُومٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5023,'لَا بَارِدٍ وَلَا كَرِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5024,'إِنَّهُمْ كَانُوا قَبْلَ ذَلِكَ مُتْرَفِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5025,'وَكَانُوا يُصِرُّونَ عَلَى الْحِنْثِ الْعَظِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5026,'وَكَانُوا يَقُولُونَ أَئِذَا مِتْنَا وَكُنَّا تُرَاباً وَعِظَاماً أَئِنَّا لَمَبْعُوثُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5027,'أَوَآَبَاؤُنَا الْأَوَّلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5028,'قُلْ إِنَّ الْأَوَّلِينَ وَالْآَخِرِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5029,'لَمَجْمُوعُونَ إِلَى مِيقَاتِ يَوْمٍ مَعْلُومٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5030,'ثُمَّ إِنَّكُمْ أَيُّهَا الضَّالُّونَ الْمُكَذِّبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5031,'لَآَكِلُونَ مِنْ شَجَرٍ مِنْ زَقُّومٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5032,'فَمَالِئُونَ مِنْهَا الْبُطُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5033,'فَشَارِبُونَ عَلَيْهِ مِنَ الْحَمِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5034,'فَشَارِبُونَ شُرْبَ الْهِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5035,'هَذَا نُزُلُهُمْ يَوْمَ الدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5036,'نَحْنُ خَلَقْنَاكُمْ فَلَوْلَا تُصَدِّقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5037,'أَفَرَأَيْتُمْ مَا تُمْنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5038,'أَأَنْتُمْ تَخْلُقُونَهُ أَمْ نَحْنُ الْخَالِقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5039,'نَحْنُ قَدَّرْنَا بَيْنَكُمُ الْمَوْتَ وَمَا نَحْنُ بِمَسْبُوقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5040,'عَلَى أَنْ نُبَدِّلَ أَمْثَالَكُمْ وَنُنْشِئَكُمْ فِي مَا لَا تَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5041,'وَلَقَدْ عَلِمْتُمُ النَّشْأَةَ الْأُولَى فَلَوْلَا تَذَكَّرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5042,'أَفَرَأَيْتُمْ مَا تَحْرُثُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5043,'أَأَنْتُمْ تَزْرَعُونَهُ أَمْ نَحْنُ الزَّارِعُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5044,'لَوْ نَشَاءُ لَجَعَلْنَاهُ حُطَاماً فَظَلْتُمْ تَفَكَّهُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5045,'إِنَّا لَمُغْرَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5046,'بَلْ نَحْنُ مَحْرُومُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5047,'أَفَرَأَيْتُمُ الْمَاءَ الَّذِي تَشْرَبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5048,'أَأَنْتُمْ أَنْزَلْتُمُوهُ مِنَ الْمُزْنِ أَمْ نَحْنُ الْمُنْزِلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5049,'لَوْ نَشَاءُ جَعَلْنَاهُ أُجَاجاً فَلَوْلَا تَشْكُرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5050,'أَفَرَأَيْتُمُ النَّارَ الَّتِي تُورُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5051,'أَأَنْتُمْ أَنْشَأْتُمْ شَجَرَتَهَا أَمْ نَحْنُ الْمُنْشِئُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5052,'نَحْنُ جَعَلْنَاهَا تَذْكِرَةً وَمَتَاعاً لِلْمُقْوِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5053,'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5054,'فَلَا أُقْسِمُ بِمَوَاقِعِ النُّجُومِ');
INSERT INTO "Quran"(id,imlai) VALUES(5055,'وَإِنَّهُ لَقَسَمٌ لَوْ تَعْلَمُونَ عَظِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5056,'إِنَّهُ لَقُرْآَنٌ كَرِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5057,'فِي كِتَابٍ مَكْنُونٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5058,'لَا يَمَسُّهُ إِلَّا الْمُطَهَّرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5059,'تَنْزِيلٌ مِنْ رَبِّ الْعَالَمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5060,'أَفَبِهَذَا الْحَدِيثِ أَنْتُمْ مُدْهِنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5061,'وَتَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5062,'فَلَوْلَا إِذَا بَلَغَتِ الْحُلْقُومَ');
INSERT INTO "Quran"(id,imlai) VALUES(5063,'وَأَنْتُمْ حِينَئِذٍ تَنْظُرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5064,'وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْكُمْ وَلَكِنْ لَا تُبْصِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5065,'فَلَوْلَا إِنْ كُنْتُمْ غَيْرَ مَدِينِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5066,'تَرْجِعُونَهَا إِنْ كُنْتُمْ صَادِقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5067,'فَأَمَّا إِنْ كَانَ مِنَ الْمُقَرَّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5068,'فَرَوْحٌ وَرَيْحَانٌ وَجَنَّةُ نَعِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5069,'وَأَمَّا إِنْ كَانَ مِنْ أَصْحَابِ الْيَمِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5070,'فَسَلَامٌ لَكَ مِنْ أَصْحَابِ الْيَمِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5071,'وَأَمَّا إِنْ كَانَ مِنَ الْمُكَذِّبِينَ الضَّالِّينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5072,'فَنُزُلٌ مِنْ حَمِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5073,'وَتَصْلِيَةُ جَحِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5074,'إِنَّ هَذَا لَهُوَ حَقُّ الْيَقِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5075,'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5076,'سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَهُوَ الْعَزِيزُ الْحَكِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5077,'لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ يُحْيِي وَيُمِيتُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5078,'هُوَ الْأَوَّلُ وَالْآَخِرُ وَالظَّاهِرُ وَالْبَاطِنُ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5079,'هُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَى عَلَى الْعَرْشِ يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاءِ وَمَا يَعْرُجُ فِيهَا وَهُوَ مَعَكُمْ أَيْنَ مَا كُنْتُمْ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5080,'لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ');
INSERT INTO "Quran"(id,imlai) VALUES(5081,'يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَهُوَ عَلِيمٌ بِذَاتِ الصُّدُورِ');
INSERT INTO "Quran"(id,imlai) VALUES(5082,'آَمِنُوا بِاللَّهِ وَرَسُولِهِ وَأَنْفِقُوا مِمَّا جَعَلَكُمْ مُسْتَخْلَفِينَ فِيهِ فَالَّذِينَ آَمَنُوا مِنْكُمْ وَأَنْفَقُوا لَهُمْ أَجْرٌ كَبِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5083,'وَمَا لَكُمْ لَا تُؤْمِنُونَ بِاللَّهِ وَالرَّسُولُ يَدْعُوكُمْ لِتُؤْمِنُوا بِرَبِّكُمْ وَقَدْ أَخَذَ مِيثَاقَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5084,'هُوَ الَّذِي يُنَزِّلُ عَلَى عَبْدِهِ آَيَاتٍ بَيِّنَاتٍ لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ وَإِنَّ اللَّهَ بِكُمْ لَرَءُوفٌ رَحِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5085,'وَمَا لَكُمْ أَلَّا تُنْفِقُوا فِي سَبِيلِ اللَّهِ وَلِلَّهِ مِيرَاثُ السَّمَاوَاتِ وَالْأَرْضِ لَا يَسْتَوِي مِنْكُمْ مَنْ أَنْفَقَ مِنْ قَبْلِ الْفَتْحِ وَقَاتَلَ أُولَئِكَ أَعْظَمُ دَرَجَةً مِنَ الَّذِينَ أَنْفَقُوا مِنْ بَعْدُ وَقَاتَلُوا وَكُلّاً وَعَدَ اللَّهُ الْحُسْنَى وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5086,'مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضاً حَسَناً فَيُضَاعِفَهُ لَهُ وَلَهُ أَجْرٌ كَرِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5087,'يَوْمَ تَرَى الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ يَسْعَى نُورُهُمْ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِمْ بُشْرَاكُمُ الْيَوْمَ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ذَلِكَ هُوَ الْفَوْزُ الْعَظِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5088,'يَوْمَ يَقُولُ الْمُنَافِقُونَ وَالْمُنَافِقَاتُ لِلَّذِينَ آَمَنُوا انْظُرُونَا نَقْتَبِسْ مِنْ نُورِكُمْ قِيلَ ارْجِعُوا وَرَاءَكُمْ فَالْتَمِسُوا نُوراً فَضُرِبَ بَيْنَهُمْ بِسُورٍ لَهُ بَابٌ بَاطِنُهُ فِيهِ الرَّحْمَةُ وَظَاهِرُهُ مِنْ قِبَلِهِ الْعَذَابُ');
INSERT INTO "Quran"(id,imlai) VALUES(5089,'يُنَادُونَهُمْ أَلَمْ نَكُنْ مَعَكُمْ قَالُوا بَلَى وَلَكِنَّكُمْ فَتَنْتُمْ أَنْفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ الْأَمَانِيُّ حَتَّى جَاءَ أَمْرُ اللَّهِ وَغَرَّكُمْ بِاللَّهِ الْغَرُورُ');
INSERT INTO "Quran"(id,imlai) VALUES(5090,'فَالْيَوْمَ لَا يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَلَا مِنَ الَّذِينَ كَفَرُوا مَأْوَاكُمُ النَّارُ هِيَ مَوْلَاكُمْ وَبِئْسَ الْمَصِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5091,'أَلَمْ يَأْنِ لِلَّذِينَ آَمَنُوا أَنْ تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللَّهِ وَمَا نَزَلَ مِنَ الْحَقِّ وَلَا يَكُونُوا كَالَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلُ فَطَالَ عَلَيْهِمُ الْأَمَدُ فَقَسَتْ قُلُوبُهُمْ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5092,'اعْلَمُوا أَنَّ اللَّهَ يُحْيِي الْأَرْضَ بَعْدَ مَوْتِهَا قَدْ بَيَّنَّا لَكُمُ الْآَيَاتِ لَعَلَّكُمْ تَعْقِلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5093,'إِنَّ الْمُصَّدِّقِينَ وَالْمُصَّدِّقَاتِ وَأَقْرَضُوا اللَّهَ قَرْضاً حَسَناً يُضَاعَفُ لَهُمْ وَلَهُمْ أَجْرٌ كَرِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5094,'وَالَّذِينَ آَمَنُوا بِاللَّهِ وَرُسُلِهِ أُولَئِكَ هُمُ الصِّدِّيقُونَ وَالشُّهَدَاءُ عِنْدَ رَبِّهِمْ لَهُمْ أَجْرُهُمْ وَنُورُهُمْ وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآَيَاتِنَا أُولَئِكَ أَصْحَابُ الْجَحِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5095,'اعْلَمُوا أَنَّمَا الْحَيَاةُ الدُّنْيَا لَعِبٌ وَلَهْوٌ وَزِينَةٌ وَتَفَاخُرٌ بَيْنَكُمْ وَتَكَاثُرٌ فِي الْأَمْوَالِ وَالْأَوْلَادِ كَمَثَلِ غَيْثٍ أَعْجَبَ الْكُفَّارَ نَبَاتُهُ ثُمَّ يَهِيجُ فَتَرَاهُ مُصْفَرّاً ثُمَّ يَكُونُ حُطَاماً وَفِي الْآَخِرَةِ عَذَابٌ شَدِيدٌ وَمَغْفِرَةٌ مِنَ اللَّهِ وَرِضْوَانٌ وَمَا الْحَيَاةُ الدُّنْيَا إِلَّا مَتَاعُ الْغُرُورِ');
INSERT INTO "Quran"(id,imlai) VALUES(5096,'سَابِقُوا إِلَى مَغْفِرَةٍ مِنْ رَبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ السَّمَاءِ وَالْأَرْضِ أُعِدَّتْ لِلَّذِينَ آَمَنُوا بِاللَّهِ وَرُسُلِهِ ذَلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5097,'مَا أَصَابَ مِنْ مُصِيبَةٍ فِي الْأَرْضِ وَلَا فِي أَنْفُسِكُمْ إِلَّا فِي كِتَابٍ مِنْ قَبْلِ أَنْ نَبْرَأَهَا إِنَّ ذَلِكَ عَلَى اللَّهِ يَسِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5098,'لِكَيْلَا تَأْسَوْا عَلَى مَا فَاتَكُمْ وَلَا تَفْرَحُوا بِمَا آَتَاكُمْ وَاللَّهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5099,'الَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ وَمَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ');
INSERT INTO "Quran"(id,imlai) VALUES(5100,'لَقَدْ أَرْسَلْنَا رُسُلَنَا بِالْبَيِّنَاتِ وَأَنْزَلْنَا مَعَهُمُ الْكِتَابَ وَالْمِيزَانَ لِيَقُومَ النَّاسُ بِالْقِسْطِ وَأَنْزَلْنَا الْحَدِيدَ فِيهِ بَأْسٌ شَدِيدٌ وَمَنَافِعُ لِلنَّاسِ وَلِيَعْلَمَ اللَّهُ مَنْ يَنْصُرُهُ وَرُسُلَهُ بِالْغَيْبِ إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5101,'وَلَقَدْ أَرْسَلْنَا نُوحاً وَإِبْرَاهِيمَ وَجَعَلْنَا فِي ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالْكِتَابَ فَمِنْهُمْ مُهْتَدٍ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5102,'ثُمَّ قَفَّيْنَا عَلَى آَثَارِهِمْ بِرُسُلِنَا وَقَفَّيْنَا بِعِيسَى ابْنِ مَرْيَمَ وَآَتَيْنَاهُ الْإِنْجِيلَ وَجَعَلْنَا فِي قُلُوبِ الَّذِينَ اتَّبَعُوهُ رَأْفَةً وَرَحْمَةً وَرَهْبَانِيَّةً ابْتَدَعُوهَا مَا كَتَبْنَاهَا عَلَيْهِمْ إِلَّا ابْتِغَاءَ رِضْوَانِ اللَّهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا فَآَتَيْنَا الَّذِينَ آَمَنُوا مِنْهُمْ أَجْرَهُمْ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5103,'يَا أَيُّهَا الَّذِينَ آَمَنُوا اتَّقُوا اللَّهَ وَآَمِنُوا بِرَسُولِهِ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَحْمَتِهِ وَيَجْعَلْ لَكُمْ نُوراً تَمْشُونَ بِهِ وَيَغْفِرْ لَكُمْ وَاللَّهُ غَفُورٌ رَحِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5104,'لِئَلَّا يَعْلَمَ أَهْلُ الْكِتَابِ أَلَّا يَقْدِرُونَ عَلَى شَيْءٍ مِنْ فَضْلِ اللَّهِ وَأَنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5105,'قَدْ سَمِعَ اللَّهُ قَوْلَ الَّتِي تُجَادِلُكَ فِي زَوْجِهَا وَتَشْتَكِي إِلَى اللَّهِ وَاللَّهُ يَسْمَعُ تَحَاوُرَكُمَا إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5106,'الَّذِينَ يُظَاهِرُونَ مِنْكُمْ مِنْ نِسَائِهِمْ مَا هُنَّ أُمَّهَاتِهِمْ إِنْ أُمَّهَاتُهُمْ إِلَّا اللَّائِي وَلَدْنَهُمْ وَإِنَّهُمْ لَيَقُولُونَ مُنْكَراً مِنَ الْقَوْلِ وَزُوراً وَإِنَّ اللَّهَ لَعَفُوٌّ غَفُورٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5107,'وَالَّذِينَ يُظَاهِرُونَ مِنْ نِسَائِهِمْ ثُمَّ يَعُودُونَ لِمَا قَالُوا فَتَحْرِيرُ رَقَبَةٍ مِنْ قَبْلِ أَنْ يَتَمَاسَّا ذَلِكُمْ تُوعَظُونَ بِهِ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5108,'فَمَنْ لَمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِنْ قَبْلِ أَنْ يَتَمَاسَّا فَمَنْ لَمْ يَسْتَطِعْ فَإِطْعَامُ سِتِّينَ مِسْكِيناً ذَلِكَ لِتُؤْمِنُوا بِاللَّهِ وَرَسُولِهِ وَتِلْكَ حُدُودُ اللَّهِ وَلِلْكَافِرِينَ عَذَابٌ أَلِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5109,'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ كُبِتُوا كَمَا كُبِتَ الَّذِينَ مِنْ قَبْلِهِمْ وَقَدْ أَنْزَلْنَا آَيَاتٍ بَيِّنَاتٍ وَلِلْكَافِرِينَ عَذَابٌ مُهِينٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5110,'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعاً فَيُنَبِّئُهُمْ بِمَا عَمِلُوا أَحْصَاهُ اللَّهُ وَنَسُوهُ وَاللَّهُ عَلَى كُلِّ شَيْءٍ شَهِيدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5111,'أَلَمْ تَرَ أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ مَا يَكُونُ مِنْ نَجْوَى ثَلَاثَةٍ إِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ إِلَّا هُوَ سَادِسُهُمْ وَلَا أَدْنَى مِنْ ذَلِكَ وَلَا أَكْثَرَ إِلَّا هُوَ مَعَهُمْ أَيْنَ مَا كَانُوا ثُمَّ يُنَبِّئُهُمْ بِمَا عَمِلُوا يَوْمَ الْقِيَامَةِ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5112,'أَلَمْ تَرَ إِلَى الَّذِينَ نُهُوا عَنِ النَّجْوَى ثُمَّ يَعُودُونَ لِمَا نُهُوا عَنْهُ وَيَتَنَاجَوْنَ بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَةِ الرَّسُولِ وَإِذَا جَاءُوكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اللَّهُ وَيَقُولُونَ فِي أَنْفُسِهِمْ لَوْلَا يُعَذِّبُنَا اللَّهُ بِمَا نَقُولُ حَسْبُهُمْ جَهَنَّمُ يَصْلَوْنَهَا فَبِئْسَ الْمَصِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5113,'يَا أَيُّهَا الَّذِينَ آَمَنُوا إِذَا تَنَاجَيْتُمْ فَلَا تَتَنَاجَوْا بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَةِ الرَّسُولِ وَتَنَاجَوْا بِالْبِرِّ وَالتَّقْوَى وَاتَّقُوا اللَّهَ الَّذِي إِلَيْهِ تُحْشَرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5114,'إِنَّمَا النَّجْوَى مِنَ الشَّيْطَانِ لِيَحْزُنَ الَّذِينَ آَمَنُوا وَلَيْسَ بِضَارِّهِمْ شَيْئاً إِلَّا بِإِذْنِ اللَّهِ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5115,'يَا أَيُّهَا الَّذِينَ آَمَنُوا إِذَا قِيلَ لَكُمْ تَفَسَّحُوا فِي الْمَجَالِسِ فَافْسَحُوا يَفْسَحِ اللَّهُ لَكُمْ وَإِذَا قِيلَ انْشُزُوا فَانْشُزُوا يَرْفَعِ اللَّهُ الَّذِينَ آَمَنُوا مِنْكُمْ وَالَّذِينَ أُوتُوا الْعِلْمَ دَرَجَاتٍ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5116,'يَا أَيُّهَا الَّذِينَ آَمَنُوا إِذَا نَاجَيْتُمُ الرَّسُولَ فَقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَةً ذَلِكَ خَيْرٌ لَكُمْ وَأَطْهَرُ فَإِنْ لَمْ تَجِدُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5117,'أَأَشْفَقْتُمْ أَنْ تُقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَاتٍ فَإِذْ لَمْ تَفْعَلُوا وَتَابَ اللَّهُ عَلَيْكُمْ فَأَقِيمُوا الصَّلَاةَ وَآَتُوا الزَّكَاةَ وَأَطِيعُوا اللَّهَ وَرَسُولَهُ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5118,'أَلَمْ تَرَ إِلَى الَّذِينَ تَوَلَّوْا قَوْماً غَضِبَ اللَّهُ عَلَيْهِمْ مَا هُمْ مِنْكُمْ وَلَا مِنْهُمْ وَيَحْلِفُونَ عَلَى الْكَذِبِ وَهُمْ يَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5119,'أَعَدَّ اللَّهُ لَهُمْ عَذَاباً شَدِيداً إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5120,'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ فَلَهُمْ عَذَابٌ مُهِينٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5121,'لَنْ تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلَا أَوْلَادُهُمْ مِنَ اللَّهِ شَيْئاً أُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5122,'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعاً فَيَحْلِفُونَ لَهُ كَمَا يَحْلِفُونَ لَكُمْ وَيَحْسَبُونَ أَنَّهُمْ عَلَى شَيْءٍ أَلَا إِنَّهُمْ هُمُ الْكَاذِبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5123,'اسْتَحْوَذَ عَلَيْهِمُ الشَّيْطَانُ فَأَنْسَاهُمْ ذِكْرَ اللَّهِ أُولَئِكَ حِزْبُ الشَّيْطَانِ أَلَا إِنَّ حِزْبَ الشَّيْطَانِ هُمُ الْخَاسِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5124,'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ أُولَئِكَ فِي الْأَذَلِّينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5125,'كَتَبَ اللَّهُ لَأَغْلِبَنَّ أَنَا وَرُسُلِي إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5126,'لَا تَجِدُ قَوْماً يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآَخِرِ يُوَادُّونَ مَنْ حَادَّ اللَّهَ وَرَسُولَهُ وَلَوْ كَانُوا آَبَاءَهُمْ أَوْ أَبْنَاءَهُمْ أَوْ إِخْوَانَهُمْ أَوْ عَشِيرَتَهُمْ أُولَئِكَ كَتَبَ فِي قُلُوبِهِمُ الْإِيمَانَ وَأَيَّدَهُمْ بِرُوحٍ مِنْهُ وَيُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ أُولَئِكَ حِزْبُ اللَّهِ أَلَا إِنَّ حِزْبَ اللَّهِ هُمُ الْمُفْلِحُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5127,'سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَهُوَ الْعَزِيزُ الْحَكِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5128,'هُوَ الَّذِي أَخْرَجَ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ مِنْ دِيَارِهِمْ لِأَوَّلِ الْحَشْرِ مَا ظَنَنْتُمْ أَنْ يَخْرُجُوا وَظَنُّوا أَنَّهُمْ مَانِعَتُهُمْ حُصُونُهُمْ مِنَ اللَّهِ فَأَتَاهُمُ اللَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ يُخْرِبُونَ بُيُوتَهُمْ بِأَيْدِيهِمْ وَأَيْدِي الْمُؤْمِنِينَ فَاعْتَبِرُوا يَا أُولِي الْأَبْصَارِ');
INSERT INTO "Quran"(id,imlai) VALUES(5129,'وَلَوْلَا أَنْ كَتَبَ اللَّهُ عَلَيْهِمُ الْجَلَاءَ لَعَذَّبَهُمْ فِي الدُّنْيَا وَلَهُمْ فِي الْآَخِرَةِ عَذَابُ النَّارِ');
INSERT INTO "Quran"(id,imlai) VALUES(5130,'ذَلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَرَسُولَهُ وَمَنْ يُشَاقِّ اللَّهَ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ');
INSERT INTO "Quran"(id,imlai) VALUES(5131,'مَا قَطَعْتُمْ مِنْ لِينَةٍ أَوْ تَرَكْتُمُوهَا قَائِمَةً عَلَى أُصُولِهَا فَبِإِذْنِ اللَّهِ وَلِيُخْزِيَ الْفَاسِقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5132,'وَمَا أَفَاءَ اللَّهُ عَلَى رَسُولِهِ مِنْهُمْ فَمَا أَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَلَا رِكَابٍ وَلَكِنَّ اللَّهَ يُسَلِّطُ رُسُلَهُ عَلَى مَنْ يَشَاءُ وَاللَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5133,'مَا أَفَاءَ اللَّهُ عَلَى رَسُولِهِ مِنْ أَهْلِ الْقُرَى فَلِلَّهِ وَلِلرَّسُولِ وَلِذِي الْقُرْبَى وَالْيَتَامَى وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ كَيْ لَا يَكُونَ دُولَةً بَيْنَ الْأَغْنِيَاءِ مِنْكُمْ وَمَا آَتَاكُمُ الرَّسُولُ فَخُذُوهُ وَمَا نَهَاكُمْ عَنْهُ فَانْتَهُوا وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ شَدِيدُ الْعِقَابِ');
INSERT INTO "Quran"(id,imlai) VALUES(5134,'لِلْفُقَرَاءِ الْمُهَاجِرِينَ الَّذِينَ أُخْرِجُوا مِنْ دِيَارِهِمْ وَأَمْوَالِهِمْ يَبْتَغُونَ فَضْلاً مِنَ اللَّهِ وَرِضْوَاناً وَيَنْصُرُونَ اللَّهَ وَرَسُولَهُ أُولَئِكَ هُمُ الصَّادِقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5135,'وَالَّذِينَ تَبَوَّءُوا الدَّارَ وَالْإِيمَانَ مِنْ قَبْلِهِمْ يُحِبُّونَ مَنْ هَاجَرَ إِلَيْهِمْ وَلَا يَجِدُونَ فِي صُدُورِهِمْ حَاجَةً مِمَّا أُوتُوا وَيُؤْثِرُونَ عَلَى أَنْفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولَئِكَ هُمُ الْمُفْلِحُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5136,'وَالَّذِينَ جَاءُوا مِنْ بَعْدِهِمْ يَقُولُونَ رَبَّنَا اغْفِرْ لَنَا وَلِإِخْوَانِنَا الَّذِينَ سَبَقُونَا بِالْإِيمَانِ وَلَا تَجْعَلْ فِي قُلُوبِنَا غِلّاً لِلَّذِينَ آَمَنُوا رَبَّنَا إِنَّكَ رَءُوفٌ رَحِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5137,'أَلَمْ تَرَ إِلَى الَّذِينَ نَافَقُوا يَقُولُونَ لِإِخْوَانِهِمُ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ لَئِنْ أُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيعُ فِيكُمْ أَحَداً أَبَداً وَإِنْ قُوتِلْتُمْ لَنَنْصُرَنَّكُمْ وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَاذِبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5138,'لَئِنْ أُخْرِجُوا لَا يَخْرُجُونَ مَعَهُمْ وَلَئِنْ قُوتِلُوا لَا يَنْصُرُونَهُمْ وَلَئِنْ نَصَرُوهُمْ لَيُوَلُّنَّ الْأَدْبَارَ ثُمَّ لَا يُنْصَرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5139,'لَأَنْتُمْ أَشَدُّ رَهْبَةً فِي صُدُورِهِمْ مِنَ اللَّهِ ذَلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5140,'لَا يُقَاتِلُونَكُمْ جَمِيعاً إِلَّا فِي قُرىً مُحَصَّنَةٍ أَوْ مِنْ وَرَاءِ جُدُرٍ بَأْسُهُمْ بَيْنَهُمْ شَدِيدٌ تَحْسَبُهُمْ جَمِيعاً وَقُلُوبُهُمْ شَتَّى ذَلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَعْقِلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5141,'كَمَثَلِ الَّذِينَ مِنْ قَبْلِهِمْ قَرِيباً ذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5142,'كَمَثَلِ الشَّيْطَانِ إِذْ قَالَ لِلْإِنْسَانِ اكْفُرْ فَلَمَّا كَفَرَ قَالَ إِنِّي بَرِيءٌ مِنْكَ إِنِّي أَخَافُ اللَّهَ رَبَّ الْعَالَمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5143,'فَكَانَ عَاقِبَتَهُمَا أَنَّهُمَا فِي النَّارِ خَالِدَيْنِ فِيهَا وَذَلِكَ جَزَاءُ الظَّالِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5144,'يَا أَيُّهَا الَّذِينَ آَمَنُوا اتَّقُوا اللَّهَ وَلْتَنْظُرْ نَفْسٌ مَا قَدَّمَتْ لِغَدٍ وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5145,'وَلَا تَكُونُوا كَالَّذِينَ نَسُوا اللَّهَ فَأَنْسَاهُمْ أَنْفُسَهُمْ أُولَئِكَ هُمُ الْفَاسِقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5146,'لَا يَسْتَوِي أَصْحَابُ النَّارِ وَأَصْحَابُ الْجَنَّةِ أَصْحَابُ الْجَنَّةِ هُمُ الْفَائِزُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5147,'لَوْ أَنْزَلْنَا هَذَا الْقُرْآَنَ عَلَى جَبَلٍ لَرَأَيْتَهُ خَاشِعاً مُتَصَدِّعاً مِنْ خَشْيَةِ اللَّهِ وَتِلْكَ الْأَمْثَالُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5148,'هُوَ اللَّهُ الَّذِي لَا إِلَهَ إِلَّا هُوَ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ هُوَ الرَّحْمَنُ الرَّحِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5149,'هُوَ اللَّهُ الَّذِي لَا إِلَهَ إِلَّا هُوَ الْمَلِكُ الْقُدُّوسُ السَّلَامُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَزِيزُ الْجَبَّارُ الْمُتَكَبِّرُ سُبْحَانَ اللَّهِ عَمَّا يُشْرِكُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5150,'هُوَ اللَّهُ الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ لَهُ الْأَسْمَاءُ الْحُسْنَى يُسَبِّحُ لَهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَهُوَ الْعَزِيزُ الْحَكِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5151,'يَا أَيُّهَا الَّذِينَ آَمَنُوا لَا تَتَّخِذُوا عَدُوِّي وَعَدُوَّكُمْ أَوْلِيَاءَ تُلْقُونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَقَدْ كَفَرُوا بِمَا جَاءَكُمْ مِنَ الْحَقِّ يُخْرِجُونَ الرَّسُولَ وَإِيَّاكُمْ أَنْ تُؤْمِنُوا بِاللَّهِ رَبِّكُمْ إِنْ كُنْتُمْ خَرَجْتُمْ جِهَاداً فِي سَبِيلِي وَابْتِغَاءَ مَرْضَاتِي تُسِرُّونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَأَنَا أَعْلَمُ بِمَا أَخْفَيْتُمْ وَمَا أَعْلَنْتُمْ وَمَنْ يَفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ');
INSERT INTO "Quran"(id,imlai) VALUES(5152,'إِنْ يَثْقَفُوكُمْ يَكُونُوا لَكُمْ أَعْدَاءً وَيَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ وَأَلْسِنَتَهُمْ بِالسُّوءِ وَوَدُّوا لَوْ تَكْفُرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5153,'لَنْ تَنْفَعَكُمْ أَرْحَامُكُمْ وَلَا أَوْلَادُكُمْ يَوْمَ الْقِيَامَةِ يَفْصِلُ بَيْنَكُمْ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5154,'قَدْ كَانَتْ لَكُمْ أُسْوَةٌ حَسَنَةٌ فِي إِبْرَاهِيمَ وَالَّذِينَ مَعَهُ إِذْ قَالُوا لِقَوْمِهِمْ إِنَّا بُرَآَءُ مِنْكُمْ وَمِمَّا تَعْبُدُونَ مِنْ دُونِ اللَّهِ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ الْعَدَاوَةُ وَالْبَغْضَاءُ أَبَداً حَتَّى تُؤْمِنُوا بِاللَّهِ وَحْدَهُ إِلَّا قَوْلَ إِبْرَاهِيمَ لِأَبِيهِ لَأَسْتَغْفِرَنَّ لَكَ وَمَا أَمْلِكُ لَكَ مِنَ اللَّهِ مِنْ شَيْءٍ رَبَّنَا عَلَيْكَ تَوَكَّلْنَا وَإِلَيْكَ أَنَبْنَا وَإِلَيْكَ الْمَصِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5155,'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِلَّذِينَ كَفَرُوا وَاغْفِرْ لَنَا رَبَّنَا إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5156,'لَقَدْ كَانَ لَكُمْ فِيهِمْ أُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآَخِرَ وَمَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ');
INSERT INTO "Quran"(id,imlai) VALUES(5157,'عَسَى اللَّهُ أَنْ يَجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذِينَ عَادَيْتُمْ مِنْهُمْ مَوَدَّةً وَاللَّهُ قَدِيرٌ وَاللَّهُ غَفُورٌ رَحِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5158,'لَا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ لَمْ يُقَاتِلُوكُمْ فِي الدِّينِ وَلَمْ يُخْرِجُوكُمْ مِنْ دِيَارِكُمْ أَنْ تَبَرُّوهُمْ وَتُقْسِطُوا إِلَيْهِمْ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5159,'إِنَّمَا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ قَاتَلُوكُمْ فِي الدِّينِ وَأَخْرَجُوكُمْ مِنْ دِيَارِكُمْ وَظَاهَرُوا عَلَى إِخْرَاجِكُمْ أَنْ تَوَلَّوْهُمْ وَمَنْ يَتَوَلَّهُمْ فَأُولَئِكَ هُمُ الظَّالِمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5160,'يَا أَيُّهَا الَّذِينَ آَمَنُوا إِذَا جَاءَكُمُ الْمُؤْمِنَاتُ مُهَاجِرَاتٍ فَامْتَحِنُوهُنَّ اللَّهُ أَعْلَمُ بِإِيمَانِهِنَّ فَإِنْ عَلِمْتُمُوهُنَّ مُؤْمِنَاتٍ فَلَا تَرْجِعُوهُنَّ إِلَى الْكُفَّارِ لَا هُنَّ حِلٌّ لَهُمْ وَلَا هُمْ يَحِلُّونَ لَهُنَّ وَآَتُوهُمْ مَا أَنْفَقُوا وَلَا جُنَاحَ عَلَيْكُمْ أَنْ تَنْكِحُوهُنَّ إِذَا آَتَيْتُمُوهُنَّ أُجُورَهُنَّ وَلَا تُمْسِكُوا بِعِصَمِ الْكَوَافِرِ وَاسْأَلُوا مَا أَنْفَقْتُمْ وَلْيَسْأَلُوا مَا أَنْفَقُوا ذَلِكُمْ حُكْمُ اللَّهِ يَحْكُمُ بَيْنَكُمْ وَاللَّهُ عَلِيمٌ حَكِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5161,'وَإِنْ فَاتَكُمْ شَيْءٌ مِنْ أَزْوَاجِكُمْ إِلَى الْكُفَّارِ فَعَاقَبْتُمْ فَآَتُوا الَّذِينَ ذَهَبَتْ أَزْوَاجُهُمْ مِثْلَ مَا أَنْفَقُوا وَاتَّقُوا اللَّهَ الَّذِي أَنْتُمْ بِهِ مُؤْمِنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5162,'يَا أَيُّهَا النَّبِيُّ إِذَا جَاءَكَ الْمُؤْمِنَاتُ يُبَايِعْنَكَ عَلَى أَنْ لَا يُشْرِكْنَ بِاللَّهِ شَيْئاً وَلَا يَسْرِقْنَ وَلَا يَزْنِينَ وَلَا يَقْتُلْنَ أَوْلَادَهُنَّ وَلَا يَأْتِينَ بِبُهْتَانٍ يَفْتَرِينَهُ بَيْنَ أَيْدِيهِنَّ وَأَرْجُلِهِنَّ وَلَا يَعْصِينَكَ فِي مَعْرُوفٍ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5163,'يَا أَيُّهَا الَّذِينَ آَمَنُوا لَا تَتَوَلَّوْا قَوْماً غَضِبَ اللَّهُ عَلَيْهِمْ قَدْ يَئِسُوا مِنَ الْآَخِرَةِ كَمَا يَئِسَ الْكُفَّارُ مِنْ أَصْحَابِ الْقُبُورِ');
INSERT INTO "Quran"(id,imlai) VALUES(5164,'سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَهُوَ الْعَزِيزُ الْحَكِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5165,'يَا أَيُّهَا الَّذِينَ آَمَنُوا لِمَ تَقُولُونَ مَا لَا تَفْعَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5166,'كَبُرَ مَقْتاً عِنْدَ اللَّهِ أَنْ تَقُولُوا مَا لَا تَفْعَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5167,'إِنَّ اللَّهَ يُحِبُّ الَّذِينَ يُقَاتِلُونَ فِي سَبِيلِهِ صَفّاً كَأَنَّهُمْ بُنْيَانٌ مَرْصُوصٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5168,'وَإِذْ قَالَ مُوسَى لِقَوْمِهِ يَا قَوْمِ لِمَ تُؤْذُونَنِي وَقَدْ تَعْلَمُونَ أَنِّي رَسُولُ اللَّهِ إِلَيْكُمْ فَلَمَّا زَاغُوا أَزَاغَ اللَّهُ قُلُوبَهُمْ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5169,'وَإِذْ قَالَ عِيسَى ابْنُ مَرْيَمَ يَا بَنِي إِسْرَائِيلَ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ مُصَدِّقاً لِمَا بَيْنَ يَدَيَّ مِنَ التَّوْرَاةِ وَمُبَشِّراً بِرَسُولٍ يَأْتِي مِنْ بَعْدِي اسْمُهُ أَحْمَدُ فَلَمَّا جَاءَهُمْ بِالْبَيِّنَاتِ قَالُوا هَذَا سِحْرٌ مُبِينٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5170,'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ الْكَذِبَ وَهُوَ يُدْعَى إِلَى الْإِسْلَامِ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5171,'يُرِيدُونَ لِيُطْفِئُوا نُورَ اللَّهِ بِأَفْوَاهِهِمْ وَاللَّهُ مُتِمُّ نُورِهِ وَلَوْ كَرِهَ الْكَافِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5172,'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَى وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَلَوْ كَرِهَ الْمُشْرِكُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5173,'يَا أَيُّهَا الَّذِينَ آَمَنُوا هَلْ أَدُلُّكُمْ عَلَى تِجَارَةٍ تُنْجِيكُمْ مِنْ عَذَابٍ أَلِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5174,'تُؤْمِنُونَ بِاللَّهِ وَرَسُولِهِ وَتُجَاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوَالِكُمْ وَأَنْفُسِكُمْ ذَلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5175,'يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَيُدْخِلْكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ وَمَسَاكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ ذَلِكَ الْفَوْزُ الْعَظِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5176,'وَأُخْرَى تُحِبُّونَهَا نَصْرٌ مِنَ اللَّهِ وَفَتْحٌ قَرِيبٌ وَبَشِّرِ الْمُؤْمِنِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5177,'يَا أَيُّهَا الَّذِينَ آَمَنُوا كُونُوا أَنْصَارَ اللَّهِ كَمَا قَالَ عِيسَى ابْنُ مَرْيَمَ لِلْحَوَارِيِّينَ مَنْ أَنْصَارِي إِلَى اللَّهِ قَالَ الْحَوَارِيُّونَ نَحْنُ أَنْصَارُ اللَّهِ فَآَمَنَتْ طَائِفَةٌ مِنْ بَنِي إِسْرَائِيلَ وَكَفَرَتْ طَائِفَةٌ فَأَيَّدْنَا الَّذِينَ آَمَنُوا عَلَى عَدُوِّهِمْ فَأَصْبَحُوا ظَاهِرِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5178,'يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ الْمَلِكِ الْقُدُّوسِ الْعَزِيزِ الْحَكِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5179,'هُوَ الَّذِي بَعَثَ فِي الْأُمِّيِّينَ رَسُولاً مِنْهُمْ يَتْلُو عَلَيْهِمْ آَيَاتِهِ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَإِنْ كَانُوا مِنْ قَبْلُ لَفِي ضَلَالٍ مُبِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5180,'وَآَخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا بِهِمْ وَهُوَ الْعَزِيزُ الْحَكِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5181,'ذَلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5182,'مَثَلُ الَّذِينَ حُمِّلُوا التَّوْرَاةَ ثُمَّ لَمْ يَحْمِلُوهَا كَمَثَلِ الْحِمَارِ يَحْمِلُ أَسْفَاراً بِئْسَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآَيَاتِ اللَّهِ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5183,'قُلْ يَا أَيُّهَا الَّذِينَ هَادُوا إِنْ زَعَمْتُمْ أَنَّكُمْ أَوْلِيَاءُ لِلَّهِ مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صَادِقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5184,'وَلَا يَتَمَنَّوْنَهُ أَبَداً بِمَا قَدَّمَتْ أَيْدِيهِمْ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5185,'قُلْ إِنَّ الْمَوْتَ الَّذِي تَفِرُّونَ مِنْهُ فَإِنَّهُ مُلَاقِيكُمْ ثُمَّ تُرَدُّونَ إِلَى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5186,'يَا أَيُّهَا الَّذِينَ آَمَنُوا إِذَا نُودِيَ لِلصَّلَاةِ مِنْ يَوْمِ الْجُمُعَةِ فَاسْعَوْا إِلَى ذِكْرِ اللَّهِ وَذَرُوا الْبَيْعَ ذَلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5187,'فَإِذَا قُضِيَتِ الصَّلَاةُ فَانْتَشِرُوا فِي الْأَرْضِ وَابْتَغُوا مِنْ فَضْلِ اللَّهِ وَاذْكُرُوا اللَّهَ كَثِيراً لَعَلَّكُمْ تُفْلِحُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5188,'وَإِذَا رَأَوْا تِجَارَةً أَوْ لَهْواً انْفَضُّوا إِلَيْهَا وَتَرَكُوكَ قَائِماً قُلْ مَا عِنْدَ اللَّهِ خَيْرٌ مِنَ اللَّهْوِ وَمِنَ التِّجَارَةِ وَاللَّهُ خَيْرُ الرَّازِقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5189,'إِذَا جَاءَكَ الْمُنَافِقُونَ قَالُوا نَشْهَدُ إِنَّكَ لَرَسُولُ اللَّهِ وَاللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُ وَاللَّهُ يَشْهَدُ إِنَّ الْمُنَافِقِينَ لَكَاذِبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5190,'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5191,'ذَلِكَ بِأَنَّهُمْ آَمَنُوا ثُمَّ كَفَرُوا فَطُبِعَ عَلَى قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5192,'وَإِذَا رَأَيْتَهُمْ تُعْجِبُكَ أَجْسَامُهُمْ وَإِنْ يَقُولُوا تَسْمَعْ لِقَوْلِهِمْ كَأَنَّهُمْ خُشُبٌ مُسَنَّدَةٌ يَحْسَبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْ هُمُ الْعَدُوُّ فَاحْذَرْهُمْ قَاتَلَهُمُ اللَّهُ أَنَّى يُؤْفَكُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5193,'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا يَسْتَغْفِرْ لَكُمْ رَسُولُ اللَّهِ لَوَّوْا رُءُوسَهُمْ وَرَأَيْتَهُمْ يَصُدُّونَ وَهُمْ مُسْتَكْبِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5194,'سَوَاءٌ عَلَيْهِمْ أَسْتَغْفَرْتَ لَهُمْ أَمْ لَمْ تَسْتَغْفِرْ لَهُمْ لَنْ يَغْفِرَ اللَّهُ لَهُمْ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5195,'هُمُ الَّذِينَ يَقُولُونَ لَا تُنْفِقُوا عَلَى مَنْ عِنْدَ رَسُولِ اللَّهِ حَتَّى يَنْفَضُّوا وَلِلَّهِ خَزَائِنُ السَّمَاوَاتِ وَالْأَرْضِ وَلَكِنَّ الْمُنَافِقِينَ لَا يَفْقَهُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5196,'يَقُولُونَ لَئِنْ رَجَعْنَا إِلَى الْمَدِينَةِ لَيُخْرِجَنَّ الْأَعَزُّ مِنْهَا الْأَذَلَّ وَلِلَّهِ الْعِزَّةُ وَلِرَسُولِهِ وَلِلْمُؤْمِنِينَ وَلَكِنَّ الْمُنَافِقِينَ لَا يَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5197,'يَا أَيُّهَا الَّذِينَ آَمَنُوا لَا تُلْهِكُمْ أَمْوَالُكُمْ وَلَا أَوْلَادُكُمْ عَنْ ذِكْرِ اللَّهِ وَمَنْ يَفْعَلْ ذَلِكَ فَأُولَئِكَ هُمُ الْخَاسِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5198,'وَأَنْفِقُوا مِنْ مَا رَزَقْنَاكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ أَحَدَكُمُ الْمَوْتُ فَيَقُولَ رَبِّ لَوْلَا أَخَّرْتَنِي إِلَى أَجَلٍ قَرِيبٍ فَأَصَّدَّقَ وَأَكُنْ مِنَ الصَّالِحِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5199,'وَلَنْ يُؤَخِّرَ اللَّهُ نَفْساً إِذَا جَاءَ أَجَلُهَا وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5200,'يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5201,'هُوَ الَّذِي خَلَقَكُمْ فَمِنْكُمْ كَافِرٌ وَمِنْكُمْ مُؤْمِنٌ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5202,'خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَإِلَيْهِ الْمَصِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5203,'يَعْلَمُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَيَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ وَاللَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ');
INSERT INTO "Quran"(id,imlai) VALUES(5204,'أَلَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ كَفَرُوا مِنْ قَبْلُ فَذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5205,'ذَلِكَ بِأَنَّهُ كَانَتْ تَأْتِيهِمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَقَالُوا أَبَشَرٌ يَهْدُونَنَا فَكَفَرُوا وَتَوَلَّوْا وَاسْتَغْنَى اللَّهُ وَاللَّهُ غَنِيٌّ حَمِيدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5206,'زَعَمَ الَّذِينَ كَفَرُوا أَنْ لَنْ يُبْعَثُوا قُلْ بَلَى وَرَبِّي لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْ وَذَلِكَ عَلَى اللَّهِ يَسِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5207,'فَآَمِنُوا بِاللَّهِ وَرَسُولِهِ وَالنُّورِ الَّذِي أَنْزَلْنَا وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5208,'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ذَلِكَ يَوْمُ التَّغَابُنِ وَمَنْ يُؤْمِنْ بِاللَّهِ وَيَعْمَلْ صَالِحاً يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَداً ذَلِكَ الْفَوْزُ الْعَظِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5209,'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآَيَاتِنَا أُولَئِكَ أَصْحَابُ النَّارِ خَالِدِينَ فِيهَا وَبِئْسَ الْمَصِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5210,'مَا أَصَابَ مِنْ مُصِيبَةٍ إِلَّا بِإِذْنِ اللَّهِ وَمَنْ يُؤْمِنْ بِاللَّهِ يَهْدِ قَلْبَهُ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5211,'وَأَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ فَإِنْ تَوَلَّيْتُمْ فَإِنَّمَا عَلَى رَسُولِنَا الْبَلَاغُ الْمُبِينُ');
INSERT INTO "Quran"(id,imlai) VALUES(5212,'اللَّهُ لَا إِلَهَ إِلَّا هُوَ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5213,'يَا أَيُّهَا الَّذِينَ آَمَنُوا إِنَّ مِنْ أَزْوَاجِكُمْ وَأَوْلَادِكُمْ عَدُوّاً لَكُمْ فَاحْذَرُوهُمْ وَإِنْ تَعْفُوا وَتَصْفَحُوا وَتَغْفِرُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5214,'إِنَّمَا أَمْوَالُكُمْ وَأَوْلَادُكُمْ فِتْنَةٌ وَاللَّهُ عِنْدَهُ أَجْرٌ عَظِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5215,'فَاتَّقُوا اللَّهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوا وَأَطِيعُوا وَأَنْفِقُوا خَيْراً لِأَنْفُسِكُمْ وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولَئِكَ هُمُ الْمُفْلِحُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5216,'إِنْ تُقْرِضُوا اللَّهَ قَرْضاً حَسَناً يُضَاعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْ وَاللَّهُ شَكُورٌ حَلِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5217,'عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيزُ الْحَكِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5218,'يَا أَيُّهَا النَّبِيُّ إِذَا طَلَّقْتُمُ النِّسَاءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحْصُوا الْعِدَّةَ وَاتَّقُوا اللَّهَ رَبَّكُمْ لَا تُخْرِجُوهُنَّ مِنْ بُيُوتِهِنَّ وَلَا يَخْرُجْنَ إِلَّا أَنْ يَأْتِينَ بِفَاحِشَةٍ مُبَيِّنَةٍ وَتِلْكَ حُدُودُ اللَّهِ وَمَنْ يَتَعَدَّ حُدُودَ اللَّهِ فَقَدْ ظَلَمَ نَفْسَهُ لَا تَدْرِي لَعَلَّ اللَّهَ يُحْدِثُ بَعْدَ ذَلِكَ أَمْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5219,'فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ فَارِقُوهُنَّ بِمَعْرُوفٍ وَأَشْهِدُوا ذَوَيْ عَدْلٍ مِنْكُمْ وَأَقِيمُوا الشَّهَادَةَ لِلَّهِ ذَلِكُمْ يُوعَظُ بِهِ مَنْ كَانَ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآَخِرِ وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مَخْرَجاً');
INSERT INTO "Quran"(id,imlai) VALUES(5220,'وَيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُ وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ إِنَّ اللَّهَ بَالِغُ أَمْرِهِ قَدْ جَعَلَ اللَّهُ لِكُلِّ شَيْءٍ قَدْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5221,'وَاللَّائِي يَئِسْنَ مِنَ الْمَحِيضِ مِنْ نِسَائِكُمْ إِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلَاثَةُ أَشْهُرٍ وَاللَّائِي لَمْ يَحِضْنَ وَأُولَاتُ الْأَحْمَالِ أَجَلُهُنَّ أَنْ يَضَعْنَ حَمْلَهُنَّ وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مِنْ أَمْرِهِ يُسْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5222,'ذَلِكَ أَمْرُ اللَّهِ أَنْزَلَهُ إِلَيْكُمْ وَمَنْ يَتَّقِ اللَّهَ يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُعْظِمْ لَهُ أَجْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5223,'أَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنْتُمْ مِنْ وُجْدِكُمْ وَلَا تُضَارُّوهُنَّ لِتُضَيِّقُوا عَلَيْهِنَّ وَإِنْ كُنَّ أُولَاتِ حَمْلٍ فَأَنْفِقُوا عَلَيْهِنَّ حَتَّى يَضَعْنَ حَمْلَهُنَّ فَإِنْ أَرْضَعْنَ لَكُمْ فَآَتُوهُنَّ أُجُورَهُنَّ وَأْتَمِرُوا بَيْنَكُمْ بِمَعْرُوفٍ وَإِنْ تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهُ أُخْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(5224,'لِيُنْفِقْ ذُو سَعَةٍ مِنْ سَعَتِهِ وَمَنْ قُدِرَ عَلَيْهِ رِزْقُهُ فَلْيُنْفِقْ مِمَّا آَتَاهُ اللَّهُ لَا يُكَلِّفُ اللَّهُ نَفْساً إِلَّا مَا آَتَاهَا سَيَجْعَلُ اللَّهُ بَعْدَ عُسْرٍ يُسْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5225,'وَكَأَيِّنْ مِنْ قَرْيَةٍ عَتَتْ عَنْ أَمْرِ رَبِّهَا وَرُسُلِهِ فَحَاسَبْنَاهَا حِسَاباً شَدِيداً وَعَذَّبْنَاهَا عَذَاباً نُكْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5226,'فَذَاقَتْ وَبَالَ أَمْرِهَا وَكَانَ عَاقِبَةُ أَمْرِهَا خُسْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5227,'أَعَدَّ اللَّهُ لَهُمْ عَذَاباً شَدِيداً فَاتَّقُوا اللَّهَ يَا أُولِي الْأَلْبَابِ الَّذِينَ آَمَنُوا قَدْ أَنْزَلَ اللَّهُ إِلَيْكُمْ ذِكْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5228,'رَسُولاً يَتْلُو عَلَيْكُمْ آَيَاتِ اللَّهِ مُبَيِّنَاتٍ لِيُخْرِجَ الَّذِينَ آَمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنَ الظُّلُمَاتِ إِلَى النُّورِ وَمَنْ يُؤْمِنْ بِاللَّهِ وَيَعْمَلْ صَالِحاً يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَداً قَدْ أَحْسَنَ اللَّهُ لَهُ رِزْقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5229,'اللَّهُ الَّذِي خَلَقَ سَبْعَ سَمَوَاتٍ وَمِنَ الْأَرْضِ مِثْلَهُنَّ يَتَنَزَّلُ الْأَمْرُ بَيْنَهُنَّ لِتَعْلَمُوا أَنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ وَأَنَّ اللَّهَ قَدْ أَحَاطَ بِكُلِّ شَيْءٍ عِلْماً');
INSERT INTO "Quran"(id,imlai) VALUES(5230,'يَا أَيُّهَا النَّبِيُّ لِمَ تُحَرِّمُ مَا أَحَلَّ اللَّهُ لَكَ تَبْتَغِي مَرْضَاةَ أَزْوَاجِكَ وَاللَّهُ غَفُورٌ رَحِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5231,'قَدْ فَرَضَ اللَّهُ لَكُمْ تَحِلَّةَ أَيْمَانِكُمْ وَاللَّهُ مَوْلَاكُمْ وَهُوَ الْعَلِيمُ الْحَكِيمُ');
INSERT INTO "Quran"(id,imlai) VALUES(5232,'وَإِذْ أَسَرَّ النَّبِيُّ إِلَى بَعْضِ أَزْوَاجِهِ حَدِيثاً فَلَمَّا نَبَّأَتْ بِهِ وَأَظْهَرَهُ اللَّهُ عَلَيْهِ عَرَّفَ بَعْضَهُ وَأَعْرَضَ عَنْ بَعْضٍ فَلَمَّا نَبَّأَهَا بِهِ قَالَتْ مَنْ أَنْبَأَكَ هَذَا قَالَ نَبَّأَنِيَ الْعَلِيمُ الْخَبِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5233,'إِنْ تَتُوبَا إِلَى اللَّهِ فَقَدْ صَغَتْ قُلُوبُكُمَا وَإِنْ تَظَاهَرَا عَلَيْهِ فَإِنَّ اللَّهَ هُوَ مَوْلَاهُ وَجِبْرِيلُ وَصَالِحُ الْمُؤْمِنِينَ وَالْمَلَائِكَةُ بَعْدَ ذَلِكَ ظَهِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5234,'عَسَى رَبُّهُ إِنْ طَلَّقَكُنَّ أَنْ يُبْدِلَهُ أَزْوَاجاً خَيْراً مِنْكُنَّ مُسْلِمَاتٍ مُؤْمِنَاتٍ قَانِتَاتٍ تَائِبَاتٍ عَابِدَاتٍ سَائِحَاتٍ ثَيِّبَاتٍ وَأَبْكَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5235,'يَا أَيُّهَا الَّذِينَ آَمَنُوا قُوا أَنْفُسَكُمْ وَأَهْلِيكُمْ نَاراً وَقُودُهَا النَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلَائِكَةٌ غِلَاظٌ شِدَادٌ لَا يَعْصُونَ اللَّهَ مَا أَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5236,'يَا أَيُّهَا الَّذِينَ كَفَرُوا لَا تَعْتَذِرُوا الْيَوْمَ إِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5237,'يَا أَيُّهَا الَّذِينَ آَمَنُوا تُوبُوا إِلَى اللَّهِ تَوْبَةً نَصُوحاً عَسَى رَبُّكُمْ أَنْ يُكَفِّرَ عَنْكُمْ سَيِّئَاتِكُمْ وَيُدْخِلَكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ يَوْمَ لَا يُخْزِي اللَّهُ النَّبِيَّ وَالَّذِينَ آَمَنُوا مَعَهُ نُورُهُمْ يَسْعَى بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِمْ يَقُولُونَ رَبَّنَا أَتْمِمْ لَنَا نُورَنَا وَاغْفِرْ لَنَا إِنَّكَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5238,'يَا أَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِقِينَ وَاغْلُظْ عَلَيْهِمْ وَمَأْوَاهُمْ جَهَنَّمُ وَبِئْسَ الْمَصِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5239,'ضَرَبَ اللَّهُ مَثَلاً لِلَّذِينَ كَفَرُوا اِمْرَأَةَ نُوحٍ وَامْرَأَةَ لُوطٍ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتَاهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اللَّهِ شَيْئاً وَقِيلَ ادْخُلَا النَّارَ مَعَ الدَّاخِلِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5240,'وَضَرَبَ اللَّهُ مَثَلاً لِلَّذِينَ آَمَنُوا اِمْرَأَةَ فِرْعَوْنَ إِذْ قَالَتْ رَبِّ ابْنِ لِي عِنْدَكَ بَيْتاً فِي الْجَنَّةِ وَنَجِّنِي مِنْ فِرْعَوْنَ وَعَمَلِهِ وَنَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5241,'وَمَرْيَمَ ابْنَتَ عِمْرَانَ الَّتِي أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهِ مِنْ رُوحِنَا وَصَدَّقَتْ بِكَلِمَاتِ رَبِّهَا وَكُتُبِهِ وَكَانَتْ مِنَ الْقَانِتِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5242,'تَبَارَكَ الَّذِي بِيَدِهِ الْمُلْكُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5243,'الَّذِي خَلَقَ الْمَوْتَ وَالْحَيَاةَ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلاً وَهُوَ الْعَزِيزُ الْغَفُورُ');
INSERT INTO "Quran"(id,imlai) VALUES(5244,'الَّذِي خَلَقَ سَبْعَ سَمَوَاتٍ طِبَاقاً مَا تَرَى فِي خَلْقِ الرَّحْمَنِ مِنْ تَفَاوُتٍ فَارْجِعِ الْبَصَرَ هَلْ تَرَى مِنْ فُطُورٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5245,'ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنْقَلِبْ إِلَيْكَ الْبَصَرُ خَاسِئاً وَهُوَ حَسِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5246,'وَلَقَدْ زَيَّنَّا السَّمَاءَ الدُّنْيَا بِمَصَابِيحَ وَجَعَلْنَاهَا رُجُوماً لِلشَّيَاطِينِ وَأَعْتَدْنَا لَهُمْ عَذَابَ السَّعِيرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5247,'وَلِلَّذِينَ كَفَرُوا بِرَبِّهِمْ عَذَابُ جَهَنَّمَ وَبِئْسَ الْمَصِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5248,'إِذَا أُلْقُوا فِيهَا سَمِعُوا لَهَا شَهِيقاً وَهِيَ تَفُورُ');
INSERT INTO "Quran"(id,imlai) VALUES(5249,'تَكَادُ تَمَيَّزُ مِنَ الْغَيْظِ كُلَّمَا أُلْقِيَ فِيهَا فَوْجٌ سَأَلَهُمْ خَزَنَتُهَا أَلَمْ يَأْتِكُمْ نَذِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5250,'قَالُوا بَلَى قَدْ جَاءَنَا نَذِيرٌ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اللَّهُ مِنْ شَيْءٍ إِنْ أَنْتُمْ إِلَّا فِي ضَلَالٍ كَبِيرٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5251,'وَقَالُوا لَوْ كُنَّا نَسْمَعُ أَوْ نَعْقِلُ مَا كُنَّا فِي أَصْحَابِ السَّعِيرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5252,'فَاعْتَرَفُوا بِذَنْبِهِمْ فَسُحْقاً لِأَصْحَابِ السَّعِيرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5253,'إِنَّ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ كَبِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5254,'وَأَسِرُّوا قَوْلَكُمْ أَوِ اجْهَرُوا بِهِ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ');
INSERT INTO "Quran"(id,imlai) VALUES(5255,'أَلَا يَعْلَمُ مَنْ خَلَقَ وَهُوَ اللَّطِيفُ الْخَبِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5256,'هُوَ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ ذَلُولاً فَامْشُوا فِي مَنَاكِبِهَا وَكُلُوا مِنْ رِزْقِهِ وَإِلَيْهِ النُّشُورُ');
INSERT INTO "Quran"(id,imlai) VALUES(5257,'أَأَمِنْتُمْ مَنْ فِي السَّمَاءِ أَنْ يَخْسِفَ بِكُمُ الْأَرْضَ فَإِذَا هِيَ تَمُورُ');
INSERT INTO "Quran"(id,imlai) VALUES(5258,'أَمْ أَمِنْتُمْ مَنْ فِي السَّمَاءِ أَنْ يُرْسِلَ عَلَيْكُمْ حَاصِباً فَسَتَعْلَمُونَ كَيْفَ نَذِيرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5259,'وَلَقَدْ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَكَيْفَ كَانَ نَكِيرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5260,'أَوَلَمْ يَرَوْا إِلَى الطَّيْرِ فَوْقَهُمْ صَافَّاتٍ وَيَقْبِضْنَ مَا يُمْسِكُهُنَّ إِلَّا الرَّحْمَنُ إِنَّهُ بِكُلِّ شَيْءٍ بَصِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5261,'أَمْ مَنْ هَذَا الَّذِي هُوَ جُنْدٌ لَكُمْ يَنْصُرُكُمْ مِنْ دُونِ الرَّحْمَنِ إِنِ الْكَافِرُونَ إِلَّا فِي غُرُورٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5262,'أَمْ مَنْ هَذَا الَّذِي يَرْزُقُكُمْ إِنْ أَمْسَكَ رِزْقَهُ بَلْ لَجُّوا فِي عُتُوٍّ وَنُفُورٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5263,'أَفَمَنْ يَمْشِي مُكِبّاً عَلَى وَجْهِهِ أَهْدَى أَمْ مَنْ يَمْشِي سَوِيّاً عَلَى صِرَاطٍ مُسْتَقِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5264,'قُلْ هُوَ الَّذِي أَنْشَأَكُمْ وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ قَلِيلاً مَا تَشْكُرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5265,'قُلْ هُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5266,'وَيَقُولُونَ مَتَى هَذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5267,'قُلْ إِنَّمَا الْعِلْمُ عِنْدَ اللَّهِ وَإِنَّمَا أَنَا نَذِيرٌ مُبِينٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5268,'فَلَمَّا رَأَوْهُ زُلْفَةً سِيئَتْ وُجُوهُ الَّذِينَ كَفَرُوا وَقِيلَ هَذَا الَّذِي كُنْتُمْ بِهِ تَدَّعُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5269,'قُلْ أَرَأَيْتُمْ إِنْ أَهْلَكَنِيَ اللَّهُ وَمَنْ مَعِيَ أَوْ رَحِمَنَا فَمَنْ يُجِيرُ الْكَافِرِينَ مِنْ عَذَابٍ أَلِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5270,'قُلْ هُوَ الرَّحْمَنُ آَمَنَّا بِهِ وَعَلَيْهِ تَوَكَّلْنَا فَسَتَعْلَمُونَ مَنْ هُوَ فِي ضَلَالٍ مُبِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5271,'قُلْ أَرَأَيْتُمْ إِنْ أَصْبَحَ مَاؤُكُمْ غَوْراً فَمَنْ يَأْتِيكُمْ بِمَاءٍ مَعِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5272,'ن وَالْقَلَمِ وَمَا يَسْطُرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5273,'مَا أَنْتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُونٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5274,'وَإِنَّ لَكَ لَأَجْراً غَيْرَ مَمْنُونٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5275,'وَإِنَّكَ لَعَلَى خُلُقٍ عَظِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5276,'فَسَتُبْصِرُ وَيُبْصِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5277,'بِأَيِّيكُمُ الْمَفْتُونُ');
INSERT INTO "Quran"(id,imlai) VALUES(5278,'إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5279,'فَلَا تُطِعِ الْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5280,'وَدُّوا لَوْ تُدْهِنُ فَيُدْهِنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5281,'وَلَا تُطِعْ كُلَّ حَلَّافٍ مَهِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5282,'هَمَّازٍ مَشَّاءٍ بِنَمِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5283,'مَنَّاعٍ لِلْخَيْرِ مُعْتَدٍ أَثِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5284,'عُتُلٍّ بَعْدَ ذَلِكَ زَنِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5285,'أَنْ كَانَ ذَا مَالٍ وَبَنِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5286,'إِذَا تُتْلَى عَلَيْهِ آَيَاتُنَا قَالَ أَسَاطِيرُ الْأَوَّلِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5287,'سَنَسِمُهُ عَلَى الْخُرْطُومِ');
INSERT INTO "Quran"(id,imlai) VALUES(5288,'إِنَّا بَلَوْنَاهُمْ كَمَا بَلَوْنَا أَصْحَابَ الْجَنَّةِ إِذْ أَقْسَمُوا لَيَصْرِمُنَّهَا مُصْبِحِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5289,'وَلَا يَسْتَثْنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5290,'فَطَافَ عَلَيْهَا طَائِفٌ مِنْ رَبِّكَ وَهُمْ نَائِمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5291,'فَأَصْبَحَتْ كَالصَّرِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5292,'فَتَنَادَوْا مُصْبِحِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5293,'أَنِ اغْدُوا عَلَى حَرْثِكُمْ إِنْ كُنْتُمْ صَارِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5294,'فَانْطَلَقُوا وَهُمْ يَتَخَافَتُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5295,'أَنْ لَا يَدْخُلَنَّهَا الْيَوْمَ عَلَيْكُمْ مِسْكِينٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5296,'وَغَدَوْا عَلَى حَرْدٍ قَادِرِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5297,'فَلَمَّا رَأَوْهَا قَالُوا إِنَّا لَضَالُّونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5298,'بَلْ نَحْنُ مَحْرُومُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5299,'قَالَ أَوْسَطُهُمْ أَلَمْ أَقُلْ لَكُمْ لَوْلَا تُسَبِّحُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5300,'قَالُوا سُبْحَانَ رَبِّنَا إِنَّا كُنَّا ظَالِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5301,'فَأَقْبَلَ بَعْضُهُمْ عَلَى بَعْضٍ يَتَلَاوَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5302,'قَالُوا يَا وَيْلَنَا إِنَّا كُنَّا طَاغِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5303,'عَسَى رَبُّنَا أَنْ يُبْدِلَنَا خَيْراً مِنْهَا إِنَّا إِلَى رَبِّنَا رَاغِبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5304,'كَذَلِكَ الْعَذَابُ وَلَعَذَابُ الْآَخِرَةِ أَكْبَرُ لَوْ كَانُوا يَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5305,'إِنَّ لِلْمُتَّقِينَ عِنْدَ رَبِّهِمْ جَنَّاتِ النَّعِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5306,'أَفَنَجْعَلُ الْمُسْلِمِينَ كَالْمُجْرِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5307,'مَا لَكُمْ كَيْفَ تَحْكُمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5308,'أَمْ لَكُمْ كِتَابٌ فِيهِ تَدْرُسُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5309,'إِنَّ لَكُمْ فِيهِ لَمَا تَخَيَّرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5310,'أَمْ لَكُمْ أَيْمَانٌ عَلَيْنَا بَالِغَةٌ إِلَى يَوْمِ الْقِيَامَةِ إِنَّ لَكُمْ لَمَا تَحْكُمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5311,'سَلْهُمْ أَيُّهُمْ بِذَلِكَ زَعِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5312,'أَمْ لَهُمْ شُرَكَاءُ فَلْيَأْتُوا بِشُرَكَائِهِمْ إِنْ كَانُوا صَادِقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5313,'يَوْمَ يُكْشَفُ عَنْ سَاقٍ وَيُدْعَوْنَ إِلَى السُّجُودِ فَلَا يَسْتَطِيعُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5314,'خَاشِعَةً أَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ وَقَدْ كَانُوا يُدْعَوْنَ إِلَى السُّجُودِ وَهُمْ سَالِمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5315,'فَذَرْنِي وَمَنْ يُكَذِّبُ بِهَذَا الْحَدِيثِ سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لَا يَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5316,'وَأُمْلِي لَهُمْ إِنَّ كَيْدِي مَتِينٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5317,'أَمْ تَسْأَلُهُمْ أَجْراً فَهُمْ مِنْ مَغْرَمٍ مُثْقَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5318,'أَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5319,'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُنْ كَصَاحِبِ الْحُوتِ إِذْ نَادَى وَهُوَ مَكْظُومٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5320,'لَوْلَا أَنْ تَدَارَكَهُ نِعْمَةٌ مِنْ رَبِّهِ لَنُبِذَ بِالْعَرَاءِ وَهُوَ مَذْمُومٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5321,'فَاجْتَبَاهُ رَبُّهُ فَجَعَلَهُ مِنَ الصَّالِحِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5322,'وَإِنْ يَكَادُ الَّذِينَ كَفَرُوا لَيُزْلِقُونَكَ بِأَبْصَارِهِمْ لَمَّا سَمِعُوا الذِّكْرَ وَيَقُولُونَ إِنَّهُ لَمَجْنُونٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5323,'وَمَا هُوَ إِلَّا ذِكْرٌ لِلْعَالَمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5324,'الْحَاقَّةُ');
INSERT INTO "Quran"(id,imlai) VALUES(5325,'مَا الْحَاقَّةُ');
INSERT INTO "Quran"(id,imlai) VALUES(5326,'وَمَا أَدْرَاكَ مَا الْحَاقَّةُ');
INSERT INTO "Quran"(id,imlai) VALUES(5327,'كَذَّبَتْ ثَمُودُ وَعَادٌ بِالْقَارِعَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5328,'فَأَمَّا ثَمُودُ فَأُهْلِكُوا بِالطَّاغِيَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5329,'وَأَمَّا عَادٌ فَأُهْلِكُوا بِرِيحٍ صَرْصَرٍ عَاتِيَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5330,'سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَثَمَانِيَةَ أَيَّامٍ حُسُوماً فَتَرَى الْقَوْمَ فِيهَا صَرْعَى كَأَنَّهُمْ أَعْجَازُ نَخْلٍ خَاوِيَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5331,'فَهَلْ تَرَى لَهُمْ مِنْ بَاقِيَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5332,'وَجَاءَ فِرْعَوْنُ وَمَنْ قَبْلَهُ وَالْمُؤْتَفِكَاتُ بِالْخَاطِئَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5333,'فَعَصَوْا رَسُولَ رَبِّهِمْ فَأَخَذَهُمْ أَخْذَةً رَابِيَةً');
INSERT INTO "Quran"(id,imlai) VALUES(5334,'إِنَّا لَمَّا طَغَى الْمَاءُ حَمَلْنَاكُمْ فِي الْجَارِيَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5335,'لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَتَعِيَهَا أُذُنٌ وَاعِيَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5336,'فَإِذَا نُفِخَ فِي الصُّورِ نَفْخَةٌ وَاحِدَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5337,'وَحُمِلَتِ الْأَرْضُ وَالْجِبَالُ فَدُكَّتَا دَكَّةً وَاحِدَةً');
INSERT INTO "Quran"(id,imlai) VALUES(5338,'فَيَوْمَئِذٍ وَقَعَتِ الْوَاقِعَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(5339,'وَانْشَقَّتِ السَّمَاءُ فَهِيَ يَوْمَئِذٍ وَاهِيَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5340,'وَالْمَلَكُ عَلَى أَرْجَائِهَا وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمَانِيَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5341,'يَوْمَئِذٍ تُعْرَضُونَ لَا تَخْفَى مِنْكُمْ خَافِيَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5342,'فَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ فَيَقُولُ هَاؤُمُ اقْرَءُوا كِتَابِيَهْ');
INSERT INTO "Quran"(id,imlai) VALUES(5343,'إِنِّي ظَنَنْتُ أَنِّي مُلَاقٍ حِسَابِيَهْ');
INSERT INTO "Quran"(id,imlai) VALUES(5344,'فَهُوَ فِي عِيشَةٍ رَاضِيَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5345,'فِي جَنَّةٍ عَالِيَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5346,'قُطُوفُهَا دَانِيَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5347,'كُلُوا وَاشْرَبُوا هَنِيئاً بِمَا أَسْلَفْتُمْ فِي الْأَيَّامِ الْخَالِيَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5348,'وَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِشِمَالِهِ فَيَقُولُ يَا لَيْتَنِي لَمْ أُوتَ كِتَابِيَهْ');
INSERT INTO "Quran"(id,imlai) VALUES(5349,'وَلَمْ أَدْرِ مَا حِسَابِيَهْ');
INSERT INTO "Quran"(id,imlai) VALUES(5350,'يَا ‎لَيْتَهَا كَانَتِ الْقَاضِيَةَ');
INSERT INTO "Quran"(id,imlai) VALUES(5351,'مَا أَغْنَى عَنِّي مَالِيَهْ');
INSERT INTO "Quran"(id,imlai) VALUES(5352,'هَلَكَ عَنِّي سُلْطَانِيَهْ');
INSERT INTO "Quran"(id,imlai) VALUES(5353,'خُذُوهُ فَغُلُّوهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5354,'ثُمَّ الْجَحِيمَ صَلُّوهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5355,'ثُمَّ فِي سِلْسِلَةٍ ذَرْعُهَا سَبْعُونَ ذِرَاعاً فَاسْلُكُوهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5356,'إِنَّهُ كَانَ لَا يُؤْمِنُ بِاللَّهِ الْعَظِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5357,'وَلَا يَحُضُّ عَلَى طَعَامِ الْمِسْكِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5358,'فَلَيْسَ لَهُ الْيَوْمَ هَاهُنَا حَمِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5359,'وَلَا طَعَامٌ إِلَّا مِنْ غِسْلِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5360,'لَا يَأْكُلُهُ إِلَّا الْخَاطِئُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5361,'فَلَا أُقْسِمُ بِمَا تُبْصِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5362,'وَمَا لَا تُبْصِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5363,'إِنَّهُ لَقَوْلُ رَسُولٍ كَرِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5364,'وَمَا هُوَ بِقَوْلِ شَاعِرٍ قَلِيلاً مَا تُؤْمِنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5365,'وَلَا بِقَوْلِ كَاهِنٍ قَلِيلاً مَا تَذَكَّرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5366,'تَنْزِيلٌ مِنْ رَبِّ الْعَالَمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5367,'وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ الْأَقَاوِيلِ');
INSERT INTO "Quran"(id,imlai) VALUES(5368,'لَأَخَذْنَا مِنْهُ بِالْيَمِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5369,'ثُمَّ لَقَطَعْنَا مِنْهُ الْوَتِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5370,'فَمَا مِنْكُمْ مِنْ أَحَدٍ عَنْهُ حَاجِزِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5371,'وَإِنَّهُ لَتَذْكِرَةٌ لِلْمُتَّقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5372,'وَإِنَّا لَنَعْلَمُ أَنَّ مِنْكُمْ مُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5373,'وَإِنَّهُ لَحَسْرَةٌ عَلَى الْكَافِرِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5374,'وَإِنَّهُ لَحَقُّ الْيَقِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5375,'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5376,'سَأَلَ سَائِلٌ بِعَذَابٍ وَاقِعٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5377,'لِلْكَافِرِينَ لَيْسَ لَهُ دَافِعٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5378,'مِنَ اللَّهِ ذِي الْمَعَارِجِ');
INSERT INTO "Quran"(id,imlai) VALUES(5379,'تَعْرُجُ الْمَلَائِكَةُ وَالرُّوحُ إِلَيْهِ فِي يَوْمٍ كَانَ مِقْدَارُهُ خَمْسِينَ أَلْفَ سَنَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5380,'فَاصْبِرْ صَبْراً جَمِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5381,'إِنَّهُمْ يَرَوْنَهُ بَعِيداً');
INSERT INTO "Quran"(id,imlai) VALUES(5382,'وَنَرَاهُ قَرِيباً');
INSERT INTO "Quran"(id,imlai) VALUES(5383,'يَوْمَ تَكُونُ السَّمَاءُ كَالْمُهْلِ');
INSERT INTO "Quran"(id,imlai) VALUES(5384,'وَتَكُونُ الْجِبَالُ كَالْعِهْنِ');
INSERT INTO "Quran"(id,imlai) VALUES(5385,'وَلَا يَسْأَلُ حَمِيمٌ حَمِيماً');
INSERT INTO "Quran"(id,imlai) VALUES(5386,'يُبَصَّرُونَهُمْ يَوَدُّ الْمُجْرِمُ لَوْ يَفْتَدِي مِنْ عَذَابِ يَوْمِئِذٍ بِبَنِيهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5387,'وَصَاحِبَتِهِ وَأَخِيهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5388,'وَفَصِيلَتِهِ الَّتِي تُؤْوِيهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5389,'وَمَنْ فِي الْأَرْضِ جَمِيعاً ثُمَّ يُنْجِيهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5390,'كَلَّا إِنَّهَا لَظَى');
INSERT INTO "Quran"(id,imlai) VALUES(5391,'نَزَّاعَةً لِلشَّوَى');
INSERT INTO "Quran"(id,imlai) VALUES(5392,'تَدْعُوا مَنْ أَدْبَرَ وَتَوَلَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5393,'وَجَمَعَ فَأَوْعَى');
INSERT INTO "Quran"(id,imlai) VALUES(5394,'إِنَّ الْإِنْسَانَ خُلِقَ هَلُوعاً');
INSERT INTO "Quran"(id,imlai) VALUES(5395,'إِذَا مَسَّهُ الشَّرُّ جَزُوعاً');
INSERT INTO "Quran"(id,imlai) VALUES(5396,'وَإِذَا مَسَّهُ الْخَيْرُ مَنُوعاً');
INSERT INTO "Quran"(id,imlai) VALUES(5397,'إِلَّا الْمُصَلِّينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5398,'الَّذِينَ هُمْ عَلَى صَلَاتِهِمْ دَائِمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5399,'وَالَّذِينَ فِي أَمْوَالِهِمْ حَقٌّ مَعْلُومٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5400,'لِلسَّائِلِ وَالْمَحْرُومِ');
INSERT INTO "Quran"(id,imlai) VALUES(5401,'وَالَّذِينَ يُصَدِّقُونَ بِيَوْمِ الدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5402,'وَالَّذِينَ هُمْ مِنْ عَذَابِ رَبِّهِمْ مُشْفِقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5403,'إِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُونٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5404,'وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5405,'إِلَّا عَلَى أَزْوَاجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5406,'فَمَنِ ابْتَغَى وَرَاءَ ذَلِكَ فَأُولَئِكَ هُمُ الْعَادُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5407,'وَالَّذِينَ هُمْ لِأَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5408,'وَالَّذِينَ هُمْ بِشَهَادَاتِهِمْ قَائِمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5409,'وَالَّذِينَ هُمْ عَلَى صَلَاتِهِمْ يُحَافِظُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5410,'أُولَئِكَ فِي جَنَّاتٍ مُكْرَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5411,'فَمَالِ الَّذِينَ كَفَرُوا قِبَلَكَ مُهْطِعِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5412,'عَنِ الْيَمِينِ وَعَنِ الشِّمَالِ عِزِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5413,'أَيَطْمَعُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُدْخَلَ جَنَّةَ نَعِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5414,'كَلَّا إِنَّا خَلَقْنَاهُمْ مِمَّا يَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5415,'فَلَا أُقْسِمُ بِرَبِّ الْمَشَارِقِ وَالْمَغَارِبِ إِنَّا لَقَادِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5416,'عَلَى أَنْ نُبَدِّلَ خَيْراً مِنْهُمْ وَمَا نَحْنُ بِمَسْبُوقِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5417,'فَذَرْهُمْ يَخُوضُوا وَيَلْعَبُوا حَتَّى يُلَاقُوا يَوْمَهُمُ الَّذِي يُوعَدُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5418,'يَوْمَ يَخْرُجُونَ مِنَ الْأَجْدَاثِ سِرَاعاً كَأَنَّهُمْ إِلَى نُصُبٍ يُوفِضُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5419,'خَاشِعَةً أَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ذَلِكَ الْيَوْمُ الَّذِي كَانُوا يُوعَدُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5420,'إِنَّا أَرْسَلْنَا نُوحاً إِلَى قَوْمِهِ أَنْ أَنْذِرْ قَوْمَكَ مِنْ قَبْلِ أَنْ يَأْتِيَهُمْ عَذَابٌ أَلِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5421,'قَالَ يَا قَوْمِ إِنِّي لَكُمْ نَذِيرٌ مُبِينٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5422,'أَنِ اعْبُدُوا اللَّهَ وَاتَّقُوهُ وَأَطِيعُونِ');
INSERT INTO "Quran"(id,imlai) VALUES(5423,'يَغْفِرْ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُؤَخِّرْكُمْ إِلَى أَجَلٍ مُسَمّىً إِنَّ أَجَلَ اللَّهِ إِذَا جَاءَ لَا يُؤَخَّرُ لَوْ كُنْتُمْ تَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5424,'قَالَ رَبِّ إِنِّي دَعَوْتُ قَوْمِي لَيْلاً وَنَهَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5425,'فَلَمْ يَزِدْهُمْ دُعَائِي إِلَّا فِرَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5426,'وَإِنِّي كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوا أَصَابِعَهُمْ فِي آَذَانِهِمْ وَاسْتَغْشَوْا ثِيَابَهُمْ وَأَصَرُّوا وَاسْتَكْبَرُوا اسْتِكْبَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5427,'ثُمَّ إِنِّي دَعَوْتُهُمْ جِهَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5428,'ثُمَّ إِنِّي أَعْلَنْتُ لَهُمْ وَأَسْرَرْتُ لَهُمْ إِسْرَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5429,'فَقُلْتُ اسْتَغْفِرُوا رَبَّكُمْ إِنَّهُ كَانَ غَفَّاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5430,'يُرْسِلِ السَّمَاءَ عَلَيْكُمْ مِدْرَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5431,'وَيُمْدِدْكُمْ بِأَمْوَالٍ وَبَنِينَ وَيَجْعَلْ لَكُمْ جَنَّاتٍ وَيَجْعَلْ لَكُمْ أَنْهَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5432,'مَا لَكُمْ لَا تَرْجُونَ لِلَّهِ وَقَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5433,'وَقَدْ خَلَقَكُمْ أَطْوَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5434,'أَلَمْ تَرَوْا كَيْفَ خَلَقَ اللَّهُ سَبْعَ سَمَوَاتٍ طِبَاقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5435,'وَجَعَلَ الْقَمَرَ فِيهِنَّ نُوراً وَجَعَلَ الشَّمْسَ سِرَاجاً');
INSERT INTO "Quran"(id,imlai) VALUES(5436,'وَاللَّهُ أَنْبَتَكُمْ مِنَ الْأَرْضِ نَبَاتاً');
INSERT INTO "Quran"(id,imlai) VALUES(5437,'ثُمَّ يُعِيدُكُمْ فِيهَا وَيُخْرِجُكُمْ إِخْرَاجاً');
INSERT INTO "Quran"(id,imlai) VALUES(5438,'وَاللَّهُ جَعَلَ لَكُمُ الْأَرْضَ بِسَاطاً');
INSERT INTO "Quran"(id,imlai) VALUES(5439,'لِتَسْلُكُوا مِنْهَا سُبُلاً فِجَاجاً');
INSERT INTO "Quran"(id,imlai) VALUES(5440,'قَالَ نُوحٌ رَبِّ إِنَّهُمْ عَصَوْنِي وَاتَّبَعُوا مَنْ لَمْ يَزِدْهُ مَالُهُ وَوَلَدُهُ إِلَّا خَسَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5441,'وَمَكَرُوا مَكْراً كُبَّاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5442,'وَقَالُوا لَا تَذَرُنَّ آَلِهَتَكُمْ وَلَا تَذَرُنَّ وَدّاً وَلَا سُوَاعاً وَلَا يَغُوثَ وَيَعُوقَ وَنَسْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5443,'وَقَدْ أَضَلُّوا كَثِيراً وَلَا تَزِدِ الظَّالِمِينَ إِلَّا ضَلَالاً');
INSERT INTO "Quran"(id,imlai) VALUES(5444,'مِمَّا خَطِيئَاتِهِمْ أُغْرِقُوا فَأُدْخِلُوا نَاراً فَلَمْ يَجِدُوا لَهُمْ مِنْ دُونِ اللَّهِ أَنْصَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5445,'وَقَالَ نُوحٌ رَبِّ لَا تَذَرْ عَلَى الْأَرْضِ مِنَ الْكَافِرِينَ دَيَّاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5446,'إِنَّكَ إِنْ تَذَرْهُمْ يُضِلُّوا عِبَادَكَ وَلَا يَلِدُوا إِلَّا فَاجِراً كَفَّاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5447,'رَبِّ اغْفِرْ لِي وَلِوَالِدَيَّ وَلِمَنْ دَخَلَ بَيْتِيَ مُؤْمِناً وَلِلْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ وَلَا تَزِدِ الظَّالِمِينَ إِلَّا تَبَاراً');
INSERT INTO "Quran"(id,imlai) VALUES(5448,'قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ فَقَالُوا إِنَّا سَمِعْنَا قُرْآَناً عَجَباً');
INSERT INTO "Quran"(id,imlai) VALUES(5449,'يَهْدِي إِلَى الرُّشْدِ فَآَمَنَّا بِهِ وَلَنْ نُشْرِكَ بِرَبِّنَا أَحَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5450,'وَأَنَّهُ تَعَالَى جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَلَا وَلَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5451,'وَأَنَّهُ كَانَ يَقُولُ سَفِيهُنَا عَلَى اللَّهِ شَطَطاً');
INSERT INTO "Quran"(id,imlai) VALUES(5452,'وَأَنَّا ظَنَنَّا أَنْ لَنْ تَقُولَ الْإِنْسُ وَالْجِنُّ عَلَى اللَّهِ كَذِباً');
INSERT INTO "Quran"(id,imlai) VALUES(5453,'وَأَنَّهُ كَانَ رِجَالٌ مِنَ الْإِنْسِ يَعُوذُونَ بِرِجَالٍ مِنَ الْجِنِّ فَزَادُوهُمْ رَهَقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5454,'وَأَنَّهُمْ ظَنُّوا كَمَا ظَنَنْتُمْ أَنْ لَنْ يَبْعَثَ اللَّهُ أَحَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5455,'وَأَنَّا لَمَسْنَا السَّمَاءَ فَوَجَدْنَاهَا مُلِئَتْ حَرَساً شَدِيداً وَشُهُباً');
INSERT INTO "Quran"(id,imlai) VALUES(5456,'وَأَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَاعِدَ لِلسَّمْعِ فَمَنْ يَسْتَمِعِ الْآَنَ يَجِدْ لَهُ شِهَاباً رَصَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5457,'وَأَنَّا لَا نَدْرِي أَشَرٌّ أُرِيدَ بِمَنْ فِي الْأَرْضِ أَمْ أَرَادَ بِهِمْ رَبُّهُمْ رَشَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5458,'وَأَنَّا مِنَّا الصَّالِحُونَ وَمِنَّا دُونَ ذَلِكَ كُنَّا طَرَائِقَ قِدَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5459,'وَأَنَّا ظَنَنَّا أَنْ لَنْ نُعجِزَ اللَّهَ فِي الْأَرْضِ وَلَنْ نُعْجِزَهُ هَرَباً');
INSERT INTO "Quran"(id,imlai) VALUES(5460,'وَأَنَّا لَمَّا سَمِعْنَا الْهُدَى آَمَنَّا بِهِ فَمَنْ يُؤْمِنْ بِرَبِّهِ فَلَا يَخَافُ بَخْساً وَلَا رَهَقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5461,'وَأَنَّا مِنَّا الْمُسْلِمُونَ وَمِنَّا الْقَاسِطُونَ فَمَنْ أَسْلَمَ فَأُولَئِكَ تَحَرَّوْا رَشَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5462,'وَأَمَّا الْقَاسِطُونَ فَكَانُوا لِجَهَنَّمَ حَطَباً');
INSERT INTO "Quran"(id,imlai) VALUES(5463,'وَأَنْ لَوِ اسْتَقَامُوا عَلَى الطَّرِيقَةِ لَأَسْقَيْنَاهُمْ مَاءً غَدَقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5464,'لِنَفْتِنَهُمْ فِيهِ وَمَنْ يُعْرِضْ عَنْ ذِكْرِ رَبِّهِ يَسْلُكْهُ عَذَاباً صَعَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5465,'وَأَنَّ الْمَسَاجِدَ لِلَّهِ فَلَا تَدْعُوا مَعَ اللَّهِ أَحَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5466,'وَأَنَّهُ لَمَّا قَامَ عَبْدُ اللَّهِ يَدْعُوهُ كَادُوا يَكُونُونَ عَلَيْهِ لِبَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5467,'قُلْ إِنَّمَا أَدْعُو رَبِّي وَلَا أُشْرِكُ بِهِ أَحَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5468,'قُلْ إِنِّي لَا أَمْلِكُ لَكُمْ ضَرّاً وَلَا رَشَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5469,'قُلْ إِنِّي لَنْ يُجِيرَنِي مِنَ اللَّهِ أَحَدٌ وَلَنْ أَجِدَ مِنْ دُونِهِ مُلْتَحَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5470,'إِلَّا بَلَاغاً مِنَ اللَّهِ وَرِسَالَاتِهِ وَمَنْ يَعْصِ اللَّهَ وَرَسُولَهُ فَإِنَّ لَهُ نَارَ جَهَنَّمَ خَالِدِينَ فِيهَا أَبَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5471,'حَتَّى إِذَا رَأَوْا مَا يُوعَدُونَ فَسَيَعْلَمُونَ مَنْ أَضْعَفُ نَاصِراً وَأَقَلُّ عَدَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5472,'قُلْ إِنْ أَدْرِي أَقَرِيبٌ مَا تُوعَدُونَ أَمْ يَجْعَلُ لَهُ رَبِّي أَمَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5473,'عَالِمُ الْغَيْبِ فَلَا يُظْهِرُ عَلَى غَيْبِهِ أَحَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5474,'إِلَّا مَنِ ارْتَضَى مِنْ رَسُولٍ فَإِنَّهُ يَسْلُكُ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ رَصَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5475,'لِيَعْلَمَ أَنْ قَدْ أَبْلَغُوا رِسَالَاتِ رَبِّهِمْ وَأَحَاطَ بِمَا لَدَيْهِمْ وَأَحْصَى كُلَّ شَيْءٍ عَدَداً');
INSERT INTO "Quran"(id,imlai) VALUES(5476,'يَا أَيُّهَا الْمُزَّمِّلُ');
INSERT INTO "Quran"(id,imlai) VALUES(5477,'قُمِ اللَّيْلَ إِلَّا قَلِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5478,'نِصْفَهُ أَوِ انْقُصْ مِنْهُ قَلِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5479,'أَوْ زِدْ عَلَيْهِ وَرَتِّلِ الْقُرْآَنَ تَرْتِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5480,'إِنَّا سَنُلْقِي عَلَيْكَ قَوْلاً ثَقِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5481,'إِنَّ نَاشِئَةَ اللَّيْلِ هِيَ أَشَدُّ وَطْئاً وَأَقْوَمُ قِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5482,'إِنَّ لَكَ فِي النَّهَارِ سَبْحاً طَوِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5483,'وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5484,'رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ لَا إِلَهَ إِلَّا هُوَ فَاتَّخِذْهُ وَكِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5485,'وَاصْبِرْ عَلَى مَا يَقُولُونَ وَاهْجُرْهُمْ هَجْراً جَمِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5486,'وَذَرْنِي وَالْمُكَذِّبِينَ أُولِي النَّعْمَةِ وَمَهِّلْهُمْ قَلِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5487,'إِنَّ لَدَيْنَا أَنْكَالاً وَجَحِيماً');
INSERT INTO "Quran"(id,imlai) VALUES(5488,'وَطَعَاماً ذَا غُصَّةٍ وَعَذَاباً أَلِيماً');
INSERT INTO "Quran"(id,imlai) VALUES(5489,'يَوْمَ تَرْجُفُ الْأَرْضُ وَالْجِبَالُ وَكَانَتِ الْجِبَالُ كَثِيباً مَهِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5490,'إِنَّا أَرْسَلْنَا إِلَيْكُمْ رَسُولاً شَاهِداً عَلَيْكُمْ كَمَا أَرْسَلْنَا إِلَى فِرْعَوْنَ رَسُولاً');
INSERT INTO "Quran"(id,imlai) VALUES(5491,'فَعَصَى فِرْعَوْنُ الرَّسُولَ فَأَخَذْنَاهُ أَخْذاً وَبِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5492,'فَكَيْفَ تَتَّقُونَ إِنْ كَفَرْتُمْ يَوْماً يَجْعَلُ الْوِلْدَانَ شِيباً');
INSERT INTO "Quran"(id,imlai) VALUES(5493,'السَّمَاءُ مُنْفَطِرٌ بِهِ كَانَ وَعْدُهُ مَفْعُولاً');
INSERT INTO "Quran"(id,imlai) VALUES(5494,'إِنَّ هَذِهِ تَذْكِرَةٌ فَمَنْ شَاءَ اتَّخَذَ إِلَى رَبِّهِ سَبِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5495,'إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنَى مِنْ ثُلُثَيِ اللَّيْلِ وَنِصْفَهُ وَثُلُثَهُ وَطَائِفَةٌ مِنَ الَّذِينَ مَعَكَ وَاللَّهُ يُقَدِّرُ اللَّيْلَ وَالنَّهَارَ عَلِمَ أَنْ لَنْ تُحْصُوهُ فَتَابَ عَلَيْكُمْ فَاقْرَءُوا مَا تَيَسَّرَ مِنَ الْقُرْآَنِ عَلِمَ أَنْ سَيَكُونُ مِنْكُمْ مَرْضَى وَآَخَرُونَ يَضْرِبُونَ فِي الْأَرْضِ يَبْتَغُونَ مِنْ فَضْلِ اللَّهِ وَآَخَرُونَ يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ فَاقْرَءُوا مَا تَيَسَّرَ مِنْهُ وَأَقِيمُوا الصَّلَاةَ وَآَتُوا الزَّكَاةَ وَأَقْرِضُوا اللَّهَ قَرْضاً حَسَناً وَمَا تُقَدِّمُوا لِأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللَّهِ هُوَ خَيْراً وَأَعْظَمَ أَجْراً وَاسْتَغْفِرُوا اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5496,'يَا أَيُّهَا الْمُدَّثِّرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5497,'قُمْ فَأَنْذِرْ');
INSERT INTO "Quran"(id,imlai) VALUES(5498,'وَرَبَّكَ فَكَبِّرْ');
INSERT INTO "Quran"(id,imlai) VALUES(5499,'وَثِيَابَكَ فَطَهِّرْ');
INSERT INTO "Quran"(id,imlai) VALUES(5500,'وَالرُّجْزَ فَاهْجُرْ');
INSERT INTO "Quran"(id,imlai) VALUES(5501,'وَلَا تَمْنُنْ تَسْتَكْثِرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5502,'وَلِرَبِّكَ فَاصْبِرْ');
INSERT INTO "Quran"(id,imlai) VALUES(5503,'فَإِذَا نُقِرَ فِي النَّاقُورِ');
INSERT INTO "Quran"(id,imlai) VALUES(5504,'فَذَلِكَ يَوْمَئِذٍ يَوْمٌ عَسِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5505,'عَلَى الْكَافِرِينَ غَيْرُ يَسِيرٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5506,'ذَرْنِي وَمَنْ خَلَقْتُ وَحِيداً');
INSERT INTO "Quran"(id,imlai) VALUES(5507,'وَجَعَلْتُ لَهُ مَالاً مَمْدُوداً');
INSERT INTO "Quran"(id,imlai) VALUES(5508,'وَبَنِينَ شُهُوداً');
INSERT INTO "Quran"(id,imlai) VALUES(5509,'وَمَهَّدْتُ لَهُ تَمْهِيداً');
INSERT INTO "Quran"(id,imlai) VALUES(5510,'ثُمَّ يَطْمَعُ أَنْ أَزِيدَ');
INSERT INTO "Quran"(id,imlai) VALUES(5511,'كَلَّا إِنَّهُ كَانَ لِآَيَاتِنَا عَنِيداً');
INSERT INTO "Quran"(id,imlai) VALUES(5512,'سَأُرْهِقُهُ صَعُوداً');
INSERT INTO "Quran"(id,imlai) VALUES(5513,'إِنَّهُ فَكَّرَ وَقَدَّرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5514,'فَقُتِلَ كَيْفَ قَدَّرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5515,'ثُمَّ قُتِلَ كَيْفَ قَدَّرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5516,'ثُمَّ نَظَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5517,'ثُمَّ عَبَسَ وَبَسَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5518,'ثُمَّ أَدْبَرَ وَاسْتَكْبَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5519,'فَقَالَ إِنْ هَذَا إِلَّا سِحْرٌ يُؤْثَرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5520,'إِنْ هَذَا إِلَّا قَوْلُ الْبَشَرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5521,'سَأُصْلِيهِ سَقَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5522,'وَمَا أَدْرَاكَ مَا سَقَرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5523,'لَا تُبْقِي وَلَا تَذَرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5524,'لَوَّاحَةٌ لِلْبَشَرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5525,'عَلَيْهَا تِسْعَةَ عَشَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5526,'وَمَا جَعَلْنَا أَصْحَابَ النَّارِ إِلَّا مَلَائِكَةً وَمَا جَعَلْنَا عِدَّتَهُمْ إِلَّا فِتْنَةً لِلَّذِينَ كَفَرُوا لِيَسْتَيْقِنَ الَّذِينَ أُوتُوا الْكِتَابَ وَيَزْدَادَ الَّذِينَ آَمَنُوا إِيمَاناً وَلَا يَرْتَابَ الَّذِينَ أُوتُوا الْكِتَابَ وَالْمُؤْمِنُونَ وَلِيَقُولَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَالْكَافِرُونَ مَاذَا أَرَادَ اللَّهُ بِهَذَا مَثَلاً كَذَلِكَ يُضِلُّ اللَّهُ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ وَمَا يَعْلَمُ جُنُودَ رَبِّكَ إِلَّا هُوَ وَمَا هِيَ إِلَّا ذِكْرَى لِلْبَشَرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5527,'كَلَّا وَالْقَمَرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5528,'وَاللَّيْلِ إِذْ أَدْبَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5529,'وَالصُّبْحِ إِذَا أَسْفَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5530,'إِنَّهَا لَإِحْدَى الْكُبَرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5531,'نَذِيراً لِلْبَشَرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5532,'لِمَنْ شَاءَ مِنْكُمْ أَنْ يَتَقَدَّمَ أَوْ يَتَأَخَّرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5533,'كُلُّ نَفْسٍ بِمَا كَسَبَتْ رَهِينَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5534,'إِلَّا أَصْحَابَ الْيَمِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5535,'فِي جَنَّاتٍ يَتَسَاءَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5536,'عَنِ الْمُجْرِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5537,'مَا سَلَكَكُمْ فِي سَقَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5538,'قَالُوا لَمْ نَكُ مِنَ الْمُصَلِّينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5539,'وَلَمْ نَكُ نُطْعِمُ الْمِسْكِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5540,'وَكُنَّا نَخُوضُ مَعَ الْخَائِضِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5541,'وَكُنَّا نُكَذِّبُ بِيَوْمِ الدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5542,'حَتَّى أَتَانَا الْيَقِينُ');
INSERT INTO "Quran"(id,imlai) VALUES(5543,'فَمَا تَنْفَعُهُمْ شَفَاعَةُ الشَّافِعِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5544,'فَمَا لَهُمْ عَنِ التَّذْكِرَةِ مُعْرِضِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5545,'كَأَنَّهُمْ حُمُرٌ مُسْتَنْفِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5546,'فَرَّتْ مِنْ قَسْوَرَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5547,'بَلْ يُرِيدُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُؤْتَى صُحُفاً مُنَشَّرَةً');
INSERT INTO "Quran"(id,imlai) VALUES(5548,'كَلَّا بَلْ لَا يَخَافُونَ الْآَخِرَةَ');
INSERT INTO "Quran"(id,imlai) VALUES(5549,'كَلَّا إِنَّهُ تَذْكِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5550,'فَمَنْ شَاءَ ذَكَرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5551,'وَمَا يَذْكُرُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ هُوَ أَهْلُ التَّقْوَى وَأَهْلُ الْمَغْفِرَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5552,'لَا أُقْسِمُ بِيَوْمِ الْقِيَامَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5553,'وَلَا أُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5554,'أَيَحْسَبُ الْإِنْسَانُ أَلَّنْ نَجْمَعَ عِظَامَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5555,'بَلَى قَادِرِينَ عَلَى أَنْ نُسَوِّيَ بَنَانَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5556,'بَلْ يُرِيدُ الْإِنْسَانُ لِيَفْجُرَ أَمَامَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5557,'يَسْأَلُ أَيَّانَ يَوْمُ الْقِيَامَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5558,'فَإِذَا بَرِقَ الْبَصَرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5559,'وَخَسَفَ الْقَمَرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5560,'وَجُمِعَ الشَّمْسُ وَالْقَمَرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5561,'يَقُولُ الْإِنْسَانُ يَوْمَئِذٍ أَيْنَ الْمَفَرُّ');
INSERT INTO "Quran"(id,imlai) VALUES(5562,'كَلَّا لَا وَزَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5563,'إِلَى رَبِّكَ يَوْمَئِذٍ الْمُسْتَقَرُّ');
INSERT INTO "Quran"(id,imlai) VALUES(5564,'يُنَبَّأُ الْإِنْسَانُ يَوْمَئِذٍ بِمَا قَدَّمَ وَأَخَّرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5565,'بَلِ الْإِنْسَانُ عَلَى نَفْسِهِ بَصِيرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5566,'وَلَوْ أَلْقَى مَعَاذِيرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5567,'لَا تُحَرِّكْ بِهِ لِسَانَكَ لِتَعْجَلَ بِهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5568,'إِنَّ عَلَيْنَا جَمْعَهُ وَقُرْآَنَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5569,'فَإِذَا قَرَأْنَاهُ فَاتَّبِعْ قُرْآَنَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5570,'ثُمَّ إِنَّ عَلَيْنَا بَيَانَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5571,'كَلَّا بَلْ تُحِبُّونَ الْعَاجِلَةَ');
INSERT INTO "Quran"(id,imlai) VALUES(5572,'وَتَذَرُونَ الْآَخِرَةَ');
INSERT INTO "Quran"(id,imlai) VALUES(5573,'وُجُوهٌ يَوْمَئِذٍ نَاضِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5574,'إِلَى رَبِّهَا نَاظِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5575,'وَوُجُوهٌ يَوْمَئِذٍ بَاسِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5576,'تَظُنُّ أَنْ يُفْعَلَ بِهَا فَاقِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5577,'كَلَّا إِذَا بَلَغَتِ التَّرَاقِيَ');
INSERT INTO "Quran"(id,imlai) VALUES(5578,'وَقِيلَ مَنْ رَاقٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5579,'وَظَنَّ أَنَّهُ الْفِرَاقُ');
INSERT INTO "Quran"(id,imlai) VALUES(5580,'وَالْتَفَّتِ السَّاقُ بِالسَّاقِ');
INSERT INTO "Quran"(id,imlai) VALUES(5581,'إِلَى رَبِّكَ يَوْمَئِذٍ الْمَسَاقُ');
INSERT INTO "Quran"(id,imlai) VALUES(5582,'فَلَا صَدَّقَ وَلَا صَلَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5583,'وَلَكِنْ كَذَّبَ وَتَوَلَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5584,'ثُمَّ ذَهَبَ إِلَى أَهْلِهِ يَتَمَطَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5585,'أَوْلَى لَكَ فَأَوْلَى');
INSERT INTO "Quran"(id,imlai) VALUES(5586,'ثُمَّ أَوْلَى لَكَ فَأَوْلَى');
INSERT INTO "Quran"(id,imlai) VALUES(5587,'أَيَحْسَبُ الْإِنْسَانُ أَنْ يُتْرَكَ سُدىً');
INSERT INTO "Quran"(id,imlai) VALUES(5588,'أَلَمْ يَكُ نُطْفَةً مِنْ مَنِيٍّ يُمْنَى');
INSERT INTO "Quran"(id,imlai) VALUES(5589,'ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5590,'فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَالْأُنْثَى');
INSERT INTO "Quran"(id,imlai) VALUES(5591,'أَلَيْسَ ذَلِكَ بِقَادِرٍ عَلَى أَنْ يُحْيِيَ الْمَوْتَى');
INSERT INTO "Quran"(id,imlai) VALUES(5592,'هَلْ أَتَى عَلَى الْإِنْسَانِ حِينٌ مِنَ الدَّهْرِ لَمْ يَكُنْ شَيْئاً مَذْكُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5593,'إِنَّا خَلَقْنَا الْإِنْسَانَ مِنْ نُطْفَةٍ أَمْشَاجٍ نَبْتَلِيهِ فَجَعَلْنَاهُ سَمِيعاً بَصِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5594,'إِنَّا هَدَيْنَاهُ السَّبِيلَ إِمَّا شَاكِراً وَإِمَّا كَفُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5595,'إِنَّا أَعْتَدْنَا لِلْكَافِرِينَ سَلَاسِلَ وَأَغْلَالاً وَسَعِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5596,'إِنَّ الْأَبْرَارَ يَشْرَبُونَ مِنْ كَأْسٍ كَانَ مِزَاجُهَا كَافُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5597,'عَيْناً يَشْرَبُ بِهَا عِبَادُ اللَّهِ يُفَجِّرُونَهَا تَفْجِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5598,'يُوفُونَ بِالنَّذْرِ وَيَخَافُونَ يَوْماً كَانَ شَرُّهُ مُسْتَطِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5599,'وَيُطْعِمُونَ الطَّعَامَ عَلَى حُبِّهِ مِسْكِيناً وَيَتِيماً وَأَسِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5600,'إِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللَّهِ لَا نُرِيدُ مِنْكُمْ جَزَاءً وَلَا شُكُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5601,'إِنَّا نَخَافُ مِنْ رَبِّنَا يَوْماً عَبُوساً قَمْطَرِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5602,'فَوَقَاهُمُ اللَّهُ شَرَّ ذَلِكَ الْيَوْمِ وَلَقَّاهُمْ نَضْرَةً وَسُرُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5603,'وَجَزَاهُمْ بِمَا صَبَرُوا جَنَّةً وَحَرِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5604,'مُتَّكِئِينَ فِيهَا عَلَى الْأَرَائِكِ لَا يَرَوْنَ فِيهَا شَمْساً وَلَا زَمْهَرِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5605,'وَدَانِيَةً عَلَيْهِمْ ظِلَالُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْلِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5606,'وَيُطَافُ عَلَيْهِمْ بِآَنِيَةٍ مِنْ فِضَّةٍ وَأَكْوَابٍ كَانَتْ قَوَارِيرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5607,'قَوَارِيرَ مِنْ فِضَّةٍ قَدَّرُوهَا تَقْدِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5608,'وَيُسْقَوْنَ فِيهَا كَأْساً كَانَ مِزَاجُهَا زَنْجَبِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5609,'عَيْناً فِيهَا تُسَمَّى سَلْسَبِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5610,'وَيَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُخَلَّدُونَ إِذَا رَأَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤاً مَنْثُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5611,'وَإِذَا رَأَيْتَ ثَمَّ رَأَيْتَ نَعِيماً وَمُلْكاً كَبِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5612,'عَالِيَهُمْ ثِيَابُ سُنْدُسٍ خُضْرٌ وَإِسْتَبْرَقٌ وَحُلُّوا أَسَاوِرَ مِنْ فِضَّةٍ وَسَقَاهُمْ رَبُّهُمْ شَرَاباً طَهُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5613,'إِنَّ هَذَا كَانَ لَكُمْ جَزَاءً وَكَانَ سَعْيُكُمْ مَشْكُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5614,'إِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ الْقُرْآَنَ تَنْزِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5615,'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ آَثِماً أَوْ كَفُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5616,'وَاذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَأَصِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5617,'وَمِنَ اللَّيْلِ فَاسْجُدْ لَهُ وَسَبِّحْهُ لَيْلاً طَوِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5618,'إِنَّ هَؤُلَاءِ يُحِبُّونَ الْعَاجِلَةَ وَيَذَرُونَ وَرَاءَهُمْ يَوْماً ثَقِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5619,'نَحْنُ خَلَقْنَاهُمْ وَشَدَدْنَا أَسْرَهُمْ وَإِذَا شِئْنَا بَدَّلْنَا أَمْثَالَهُمْ تَبْدِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5620,'إِنَّ هَذِهِ تَذْكِرَةٌ فَمَنْ شَاءَ اتَّخَذَ إِلَى رَبِّهِ سَبِيلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5621,'وَمَا تَشَاءُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ إِنَّ اللَّهَ كَانَ عَلِيماً حَكِيماً');
INSERT INTO "Quran"(id,imlai) VALUES(5622,'يُدْخِلُ مَنْ يَشَاءُ فِي رَحْمَتِهِ وَالظَّالِمِينَ أَعَدَّ لَهُمْ عَذَاباً أَلِيماً');
INSERT INTO "Quran"(id,imlai) VALUES(5623,'وَالْمُرْسَلَاتِ عُرْفاً');
INSERT INTO "Quran"(id,imlai) VALUES(5624,'فَالْعَاصِفَاتِ عَصْفاً');
INSERT INTO "Quran"(id,imlai) VALUES(5625,'وَالنَّاشِرَاتِ نَشْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5626,'فَالْفَارِقَاتِ فَرْقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5627,'فَالْمُلْقِيَاتِ ذِكْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5628,'عُذْراً أَوْ نُذْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5629,'إِنَّمَا تُوعَدُونَ لَوَاقِعٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5630,'فَإِذَا النُّجُومُ طُمِسَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5631,'وَإِذَا السَّمَاءُ فُرِجَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5632,'وَإِذَا الْجِبَالُ نُسِفَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5633,'وَإِذَا الرُّسُلُ أُقِّتَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5634,'لِأَيِّ يَوْمٍ أُجِّلَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5635,'لِيَوْمِ الْفَصْلِ');
INSERT INTO "Quran"(id,imlai) VALUES(5636,'وَمَا أَدْرَاكَ مَا يَوْمُ الْفَصْلِ');
INSERT INTO "Quran"(id,imlai) VALUES(5637,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5638,'أَلَمْ نُهْلِكِ الْأَوَّلِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5639,'ثُمَّ نُتْبِعُهُمُ الْآَخِرِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5640,'كَذَلِكَ نَفْعَلُ بِالْمُجْرِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5641,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5642,'أَلَمْ نَخْلُقْكُمْ مِنْ مَاءٍ مَهِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5643,'فَجَعَلْنَاهُ فِي قَرَارٍ مَكِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5644,'إِلَى قَدَرٍ مَعْلُومٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5645,'فَقَدَرْنَا فَنِعْمَ الْقَادِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5646,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5647,'أَلَمْ نَجْعَلِ الْأَرْضَ كِفَاتاً');
INSERT INTO "Quran"(id,imlai) VALUES(5648,'أَحْيَاءً ‎وَأَمْوَاتاً');
INSERT INTO "Quran"(id,imlai) VALUES(5649,'وَجَعَلْنَا فِيهَا رَوَاسِيَ شَامِخَاتٍ وَأَسْقَيْنَاكُمْ مَاءً فُرَاتاً');
INSERT INTO "Quran"(id,imlai) VALUES(5650,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5651,'انْطَلِقُوا إِلَى مَا كُنْتُمْ بِهِ تُكَذِّبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5652,'انْطَلِقُوا إِلَى ظِلٍّ ذِي ثَلَاثِ شُعَبٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5653,'لَا ظَلِيلٍ وَلَا يُغْنِي مِنَ اللَّهَبِ');
INSERT INTO "Quran"(id,imlai) VALUES(5654,'إِنَّهَا تَرْمِي بِشَرَرٍ كَالْقَصْرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5655,'كَأَنَّهُ جِمَالَةٌ صُفْرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5656,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5657,'هَذَا يَوْمُ لَا يَنْطِقُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5658,'وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5659,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5660,'هَذَا يَوْمُ الْفَصْلِ جَمَعْنَاكُمْ وَالْأَوَّلِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5661,'فَإِنْ كَانَ لَكُمْ كَيْدٌ فَكِيدُونِ');
INSERT INTO "Quran"(id,imlai) VALUES(5662,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5663,'إِنَّ الْمُتَّقِينَ فِي ظِلَالٍ وَعُيُونٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5664,'وَفَوَاكِهَ مِمَّا يَشْتَهُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5665,'كُلُوا وَاشْرَبُوا هَنِيئاً بِمَا كُنْتُمْ تَعْمَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5666,'إِنَّا كَذَلِكَ نَجْزِي الْمُحْسِنِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5667,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5668,'كُلُوا وَتَمَتَّعُوا قَلِيلاً إِنَّكُمْ مُجْرِمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5669,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5670,'وَإِذَا قِيلَ لَهُمُ ارْكَعُوا لَا يَرْكَعُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5671,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5672,'فَبِأَيِّ حَدِيثٍ بَعْدَهُ يُؤْمِنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5673,'عَمَّ يَتَسَاءَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5674,'عَنِ النَّبَإِ الْعَظِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5675,'الَّذِي هُمْ فِيهِ مُخْتَلِفُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5676,'كَلَّا سَيَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5677,'ثُمَّ كَلَّا سَيَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5678,'أَلَمْ نَجْعَلِ الْأَرْضَ مِهَاداً');
INSERT INTO "Quran"(id,imlai) VALUES(5679,'وَالْجِبَالَ أَوْتَاداً');
INSERT INTO "Quran"(id,imlai) VALUES(5680,'وَخَلَقْنَاكُمْ أَزْوَاجاً');
INSERT INTO "Quran"(id,imlai) VALUES(5681,'وَجَعَلْنَا نَوْمَكُمْ سُبَاتاً');
INSERT INTO "Quran"(id,imlai) VALUES(5682,'وَجَعَلْنَا اللَّيْلَ لِبَاساً');
INSERT INTO "Quran"(id,imlai) VALUES(5683,'وَجَعَلْنَا النَّهَارَ مَعَاشاً');
INSERT INTO "Quran"(id,imlai) VALUES(5684,'وَبَنَيْنَا فَوْقَكُمْ سَبْعاً شِدَاداً');
INSERT INTO "Quran"(id,imlai) VALUES(5685,'وَجَعَلْنَا سِرَاجاً وَهَّاجاً');
INSERT INTO "Quran"(id,imlai) VALUES(5686,'وَأَنْزَلْنَا مِنَ الْمُعْصِرَاتِ مَاءً ثَجَّاجاً');
INSERT INTO "Quran"(id,imlai) VALUES(5687,'لِنُخْرِجَ بِهِ حَبّاً وَنَبَاتاً');
INSERT INTO "Quran"(id,imlai) VALUES(5688,'وَجَنَّاتٍ أَلْفَافاً');
INSERT INTO "Quran"(id,imlai) VALUES(5689,'إِنَّ يَوْمَ الْفَصْلِ كَانَ مِيقَاتاً');
INSERT INTO "Quran"(id,imlai) VALUES(5690,'يَوْمَ يُنْفَخُ فِي الصُّورِ فَتَأْتُونَ أَفْوَاجاً');
INSERT INTO "Quran"(id,imlai) VALUES(5691,'وَفُتِحَتِ السَّمَاءُ فَكَانَتْ أَبْوَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5692,'وَسُيِّرَتِ الْجِبَالُ فَكَانَتْ سَرَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5693,'إِنَّ جَهَنَّمَ كَانَتْ مِرْصَاداً');
INSERT INTO "Quran"(id,imlai) VALUES(5694,'لِلطَّاغِينَ مَآَباً');
INSERT INTO "Quran"(id,imlai) VALUES(5695,'لَابِثِينَ فِيهَا أَحْقَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5696,'لَا يَذُوقُونَ فِيهَا بَرْداً وَلَا شَرَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5697,'إِلَّا حَمِيماً وَغَسَّاقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5698,'جَزَاءً وِفَاقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5699,'إِنَّهُمْ كَانُوا لَا يَرْجُونَ حِسَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5700,'وَكَذَّبُوا بِآَيَاتِنَا كِذَّاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5701,'وَكُلَّ شَيْءٍ أَحْصَيْنَاهُ كِتَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5702,'فَذُوقُوا فَلَنْ نَزِيدَكُمْ إِلَّا عَذَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5703,'إِنَّ لِلْمُتَّقِينَ مَفَازاً');
INSERT INTO "Quran"(id,imlai) VALUES(5704,'حَدَائِقَ وَأَعْنَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5705,'وَكَوَاعِبَ أَتْرَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5706,'وَكَأْساً دِهَاقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5707,'لَا يَسْمَعُونَ فِيهَا لَغْواً وَلَا كِذَّاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5708,'جَزَاءً مِنْ رَبِّكَ عَطَاءً حِسَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5709,'رَبِّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا الرَّحْمَنِ لَا يَمْلِكُونَ مِنْهُ خِطَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5710,'يَوْمَ يَقُومُ الرُّوحُ وَالْمَلَائِكَةُ صَفّاً لَا يَتَكَلَّمُونَ إِلَّا مَنْ أَذِنَ لَهُ الرَّحْمَنُ وَقَالَ صَوَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5711,'ذَلِكَ الْيَوْمُ الْحَقُّ فَمَنْ شَاءَ اتَّخَذَ إِلَى رَبِّهِ مَآَباً');
INSERT INTO "Quran"(id,imlai) VALUES(5712,'إِنَّا أَنْذَرْنَاكُمْ عَذَاباً قَرِيباً يَوْمَ يَنْظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُولُ الْكَافِرُ يَا لَيْتَنِي كُنْتُ تُرَاباً');
INSERT INTO "Quran"(id,imlai) VALUES(5713,'وَالنَّازِعَاتِ غَرْقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5714,'وَالنَّاشِطَاتِ نَشْطاً');
INSERT INTO "Quran"(id,imlai) VALUES(5715,'وَالسَّابِحَاتِ سَبْحاً');
INSERT INTO "Quran"(id,imlai) VALUES(5716,'فَالسَّابِقَاتِ سَبْقاً');
INSERT INTO "Quran"(id,imlai) VALUES(5717,'فَالْمُدَبِّرَاتِ أَمْراً');
INSERT INTO "Quran"(id,imlai) VALUES(5718,'يَوْمَ تَرْجُفُ الرَّاجِفَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(5719,'تَتْبَعُهَا الرَّادِفَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(5720,'قُلُوبٌ يَوْمَئِذٍ وَاجِفَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5721,'أَبْصَارُهَا خَاشِعَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5722,'يَقُولُونَ أَئِنَّا لَمَرْدُودُونَ فِي الْحَافِرَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5723,'أَئِذَا كُنَّا عِظَاماً نَخِرَةً');
INSERT INTO "Quran"(id,imlai) VALUES(5724,'قَالُوا تِلْكَ إِذاً كَرَّةٌ خَاسِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5725,'فَإِنَّمَا هِيَ زَجْرَةٌ وَاحِدَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5726,'فَإِذَا هُمْ بِالسَّاهِرَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5727,'هَلْ أَتَاكَ حَدِيثُ مُوسَى');
INSERT INTO "Quran"(id,imlai) VALUES(5728,'إِذْ نَادَاهُ رَبُّهُ بِالْوَادِ الْمُقَدَّسِ طُوىً');
INSERT INTO "Quran"(id,imlai) VALUES(5729,'اذْهَبْ إِلَى فِرْعَوْنَ إِنَّهُ طَغَى');
INSERT INTO "Quran"(id,imlai) VALUES(5730,'فَقُلْ هَلْ لَكَ إِلَى أَنْ تَزَكَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5731,'وَأَهْدِيَكَ إِلَى رَبِّكَ فَتَخْشَى');
INSERT INTO "Quran"(id,imlai) VALUES(5732,'فَأَرَاهُ الْآَيَةَ الْكُبْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(5733,'فَكَذَّبَ وَعَصَى');
INSERT INTO "Quran"(id,imlai) VALUES(5734,'ثُمَّ أَدْبَرَ يَسْعَى');
INSERT INTO "Quran"(id,imlai) VALUES(5735,'فَحَشَرَ فَنَادَى');
INSERT INTO "Quran"(id,imlai) VALUES(5736,'فَقَالَ أَنَا رَبُّكُمُ الْأَعْلَى');
INSERT INTO "Quran"(id,imlai) VALUES(5737,'فَأَخَذَهُ اللَّهُ نَكَالَ الْآَخِرَةِ وَالْأُولَى');
INSERT INTO "Quran"(id,imlai) VALUES(5738,'إِنَّ فِي ذَلِكَ لَعِبْرَةً لِمَنْ يَخْشَى');
INSERT INTO "Quran"(id,imlai) VALUES(5739,'أَأَنْتُمْ أَشَدُّ خَلْقاً أَمِ السَّمَاءُ بَنَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5740,'رَفَعَ سَمْكَهَا فَسَوَّاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5741,'وَأَغْطَشَ لَيْلَهَا وَأَخْرَجَ ضُحَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5742,'وَالْأَرْضَ بَعْدَ ذَلِكَ دَحَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5743,'أَخْرَجَ مِنْهَا مَاءَهَا وَمَرْعَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5744,'وَالْجِبَالَ أَرْسَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5745,'مَتَاعاً لَكُمْ وَلِأَنْعَامِكُمْ');
INSERT INTO "Quran"(id,imlai) VALUES(5746,'فَإِذَا جَاءَتِ الطَّامَّةُ الْكُبْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(5747,'يَوْمَ يَتَذَكَّرُ الْإِنْسَانُ مَا سَعَى');
INSERT INTO "Quran"(id,imlai) VALUES(5748,'وَبُرِّزَتِ الْجَحِيمُ لِمَنْ يَرَى');
INSERT INTO "Quran"(id,imlai) VALUES(5749,'فَأَمَّا مَنْ طَغَى');
INSERT INTO "Quran"(id,imlai) VALUES(5750,'وَآَثَرَ الْحَيَاةَ الدُّنْيَا');
INSERT INTO "Quran"(id,imlai) VALUES(5751,'فَإِنَّ الْجَحِيمَ هِيَ الْمَأْوَى');
INSERT INTO "Quran"(id,imlai) VALUES(5752,'وَأَمَّا مَنْ خَافَ مَقَامَ رَبِّهِ وَنَهَى النَّفْسَ عَنِ الْهَوَى');
INSERT INTO "Quran"(id,imlai) VALUES(5753,'فَإِنَّ الْجَنَّةَ هِيَ الْمَأْوَى');
INSERT INTO "Quran"(id,imlai) VALUES(5754,'يَسْأَلُونَكَ عَنِ السَّاعَةِ أَيَّانَ مُرْسَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5755,'فِيمَ أَنْتَ مِنْ ذِكْرَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5756,'إِلَى رَبِّكَ مُنْتَهَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5757,'إِنَّمَا أَنْتَ مُنْذِرُ مَنْ يَخْشَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5758,'كَأَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوا إِلَّا عَشِيَّةً أَوْ ضُحَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(5759,'عَبَسَ وَتَوَلَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5760,'أَنْ جَاءَهُ الْأَعْمَى');
INSERT INTO "Quran"(id,imlai) VALUES(5761,'وَمَا يُدْرِيكَ لَعَلَّهُ يَزَّكَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5762,'أَوْ يَذَّكَّرُ فَتَنْفَعَهُ الذِّكْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(5763,'أَمَّا مَنِ اسْتَغْنَى');
INSERT INTO "Quran"(id,imlai) VALUES(5764,'فَأَنْتَ لَهُ تَصَدَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5765,'وَمَا عَلَيْكَ أَلَّا يَزَّكَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5766,'وَأَمَّا مَنْ جَاءَكَ يَسْعَى');
INSERT INTO "Quran"(id,imlai) VALUES(5767,'وَهُوَ يَخْشَى');
INSERT INTO "Quran"(id,imlai) VALUES(5768,'فَأَنْتَ عَنْهُ تَلَهَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5769,'كَلَّا إِنَّهَا تَذْكِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5770,'فَمَنْ شَاءَ ذَكَرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5771,'فِي صُحُفٍ مُكَرَّمَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5772,'مَرْفُوعَةٍ مُطَهَّرَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5773,'بِأَيْدِي سَفَرَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5774,'كِرَامٍ بَرَرَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5775,'قُتِلَ الْإِنْسَانُ مَا أَكْفَرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5776,'مِنْ أَيِّ شَيْءٍ خَلَقَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5777,'مِنْ نُطْفَةٍ خَلَقَهُ فَقَدَّرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5778,'ثُمَّ السَّبِيلَ يَسَّرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5779,'ثُمَّ أَمَاتَهُ فَأَقْبَرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5780,'ثُمَّ إِذَا شَاءَ أَنْشَرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5781,'كَلَّا لَمَّا يَقْضِ مَا أَمَرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(5782,'فَلْيَنْظُرِ الْإِنْسَانُ إِلَى طَعَامِهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5783,'أَنَّا صَبَبْنَا الْمَاءَ صَبّاً');
INSERT INTO "Quran"(id,imlai) VALUES(5784,'ثُمَّ شَقَقْنَا الْأَرْضَ شَقّاً');
INSERT INTO "Quran"(id,imlai) VALUES(5785,'فَأَنْبَتْنَا فِيهَا حَبّاً');
INSERT INTO "Quran"(id,imlai) VALUES(5786,'وَعِنَباً وَقَضْباً');
INSERT INTO "Quran"(id,imlai) VALUES(5787,'وَزَيْتُوناً وَنَخْلاً');
INSERT INTO "Quran"(id,imlai) VALUES(5788,'وَحَدَائِقَ غُلْباً');
INSERT INTO "Quran"(id,imlai) VALUES(5789,'وَفَاكِهَةً وَأَبّاً');
INSERT INTO "Quran"(id,imlai) VALUES(5790,'مَتَاعاً لَكُمْ وَلِأَنْعَامِكُمْ');
INSERT INTO "Quran"(id,imlai) VALUES(5791,'فَإِذَا جَاءَتِ الصَّاخَّةُ');
INSERT INTO "Quran"(id,imlai) VALUES(5792,'يَوْمَ يَفِرُّ الْمَرْءُ مِنْ أَخِيهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5793,'وَأُمِّهِ وَأَبِيهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5794,'وَصَاحِبَتِهِ وَبَنِيهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5795,'لِكُلِّ امْرِئٍ مِنْهُمْ يَوْمَئِذٍ شَأْنٌ يُغْنِيهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5796,'وُجُوهٌ يَوْمَئِذٍ مُسْفِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5797,'ضَاحِكَةٌ مُسْتَبْشِرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5798,'وَوُجُوهٌ يَوْمَئِذٍ عَلَيْهَا غَبَرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5799,'تَرْهَقُهَا قَتَرَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5800,'أُولَئِكَ هُمُ الْكَفَرَةُ الْفَجَرَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(5801,'إِذَا الشَّمْسُ كُوِّرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5802,'وَإِذَا النُّجُومُ انْكَدَرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5803,'وَإِذَا الْجِبَالُ سُيِّرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5804,'وَإِذَا الْعِشَارُ عُطِّلَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5805,'وَإِذَا الْوُحُوشُ حُشِرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5806,'وَإِذَا الْبِحَارُ سُجِّرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5807,'وَإِذَا النُّفُوسُ زُوِّجَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5808,'وَإِذَا الْمَوْءُودَةُ سُئِلَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5809,'بِأَيِّ ذَنْبٍ قُتِلَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5810,'وَإِذَا الصُّحُفُ نُشِرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5811,'وَإِذَا السَّمَاءُ كُشِطَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5812,'وَإِذَا الْجَحِيمُ سُعِّرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5813,'وَإِذَا الْجَنَّةُ أُزْلِفَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5814,'عَلِمَتْ نَفْسٌ مَا أَحْضَرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5815,'فَلَا أُقْسِمُ بِالْخُنَّسِ');
INSERT INTO "Quran"(id,imlai) VALUES(5816,'الْجَوَارِ الْكُنَّسِ');
INSERT INTO "Quran"(id,imlai) VALUES(5817,'وَاللَّيْلِ إِذَا عَسْعَسَ');
INSERT INTO "Quran"(id,imlai) VALUES(5818,'وَالصُّبْحِ إِذَا تَنَفَّسَ');
INSERT INTO "Quran"(id,imlai) VALUES(5819,'إِنَّهُ لَقَوْلُ رَسُولٍ كَرِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5820,'ذِي قُوَّةٍ عِنْدَ ذِي الْعَرْشِ مَكِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5821,'مُطَاعٍ ثَمَّ أَمِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5822,'وَمَا صَاحِبُكُمْ بِمَجْنُونٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5823,'وَلَقَدْ رَآَهُ بِالْأُفُقِ الْمُبِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5824,'وَمَا هُوَ عَلَى الْغَيْبِ بِضَنِينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5825,'وَمَا هُوَ بِقَوْلِ شَيْطَانٍ رَجِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5826,'فَأَيْنَ تَذْهَبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5827,'إِنْ هُوَ إِلَّا ذِكْرٌ لِلْعَالَمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5828,'لِمَنْ شَاءَ مِنْكُمْ أَنْ يَسْتَقِيمَ');
INSERT INTO "Quran"(id,imlai) VALUES(5829,'وَمَا تَشَاءُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ رَبُّ الْعَالَمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5830,'إِذَا السَّمَاءُ انْفَطَرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5831,'وَإِذَا الْكَوَاكِبُ انْتَثَرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5832,'وَإِذَا الْبِحَارُ فُجِّرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5833,'وَإِذَا الْقُبُورُ بُعْثِرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5834,'عَلِمَتْ نَفْسٌ مَا قَدَّمَتْ وَأَخَّرَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5835,'يَا أَيُّهَا الْإِنْسَانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5836,'الَّذِي خَلَقَكَ فَسَوَّاكَ فَعَدَلَكَ');
INSERT INTO "Quran"(id,imlai) VALUES(5837,'فِي أَيِّ صُورَةٍ مَا شَاءَ رَكَّبَكَ');
INSERT INTO "Quran"(id,imlai) VALUES(5838,'كَلَّا بَلْ تُكَذِّبُونَ بِالدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5839,'وَإِنَّ عَلَيْكُمْ لَحَافِظِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5840,'كِرَاماً كَاتِبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5841,'يَعْلَمُونَ مَا تَفْعَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5842,'إِنَّ الْأَبْرَارَ لَفِي نَعِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5843,'وَإِنَّ الْفُجَّارَ لَفِي جَحِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5844,'يَصْلَوْنَهَا يَوْمَ الدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5845,'وَمَا هُمْ عَنْهَا بِغَائِبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5846,'وَمَا أَدْرَاكَ مَا يَوْمُ الدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5847,'ثُمَّ مَا أَدْرَاكَ مَا يَوْمُ الدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5848,'يَوْمَ لَا تَمْلِكُ نَفْسٌ لِنَفْسٍ شَيْئاً وَالْأَمْرُ يَوْمَئِذٍ لِلَّهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5849,'وَيْلٌ لِلْمُطَفِّفِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5850,'الَّذِينَ إِذَا اكْتَالُوا عَلَى النَّاسِ يَسْتَوْفُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5851,'وَإِذَا كَالُوهُمْ أَوْ وَزَنُوهُمْ يُخْسِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5852,'أَلَا يَظُنُّ أُولَئِكَ أَنَّهُمْ مَبْعُوثُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5853,'لِيَوْمٍ عَظِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5854,'يَوْمَ يَقُومُ النَّاسُ لِرَبِّ الْعَالَمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5855,'كَلَّا إِنَّ كِتَابَ الْفُجَّارِ لَفِي سِجِّينٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5856,'وَمَا أَدْرَاكَ مَا سِجِّينٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5857,'كِتَابٌ مَرْقُومٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5858,'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5859,'الَّذِينَ يُكَذِّبُونَ بِيَوْمِ الدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(5860,'وَمَا يُكَذِّبُ بِهِ إِلَّا كُلُّ مُعْتَدٍ أَثِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5861,'إِذَا تُتْلَى عَلَيْهِ آَيَاتُنَا قَالَ أَسَاطِيرُ الْأَوَّلِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5862,'كَلَّا بَلْ رَانَ عَلَى قُلُوبِهِمْ مَا كَانُوا يَكْسِبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5863,'كَلَّا إِنَّهُمْ عَنْ رَبِّهِمْ يَوْمَئِذٍ لَمَحْجُوبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5864,'ثُمَّ إِنَّهُمْ لَصَالُو الْجَحِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5865,'ثُمَّ يُقَالُ هَذَا الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5866,'كَلَّا إِنَّ كِتَابَ الْأَبْرَارِ لَفِي عِلِّيِّينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5867,'وَمَا أَدْرَاكَ مَا عِلِّيُّونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5868,'كِتَابٌ مَرْقُومٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5869,'يَشْهَدُهُ الْمُقَرَّبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5870,'إِنَّ الْأَبْرَارَ لَفِي نَعِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5871,'عَلَى الْأَرَائِكِ يَنْظُرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5872,'تَعْرِفُ فِي وُجُوهِهِمْ نَضْرَةَ النَّعِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(5873,'يُسْقَوْنَ مِنْ رَحِيقٍ مَخْتُومٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5874,'خِتَامُهُ مِسْكٌ وَفِي ذَلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5875,'وَمِزَاجُهُ مِنْ تَسْنِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5876,'عَيْناً يَشْرَبُ بِهَا الْمُقَرَّبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5877,'إِنَّ الَّذِينَ أَجْرَمُوا كَانُوا مِنَ الَّذِينَ آَمَنُوا يَضْحَكُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5878,'وَإِذَا مَرُّوا بِهِمْ يَتَغَامَزُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5879,'وَإِذَا انْقَلَبُوا إِلَى أَهْلِهِمُ انْقَلَبُوا فَكِهِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5880,'وَإِذَا رَأَوْهُمْ قَالُوا إِنَّ هَؤُلَاءِ لَضَالُّونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5881,'وَمَا أُرْسِلُوا عَلَيْهِمْ حَافِظِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(5882,'فَالْيَوْمَ الَّذِينَ آَمَنُوا مِنَ الْكُفَّارِ يَضْحَكُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5883,'عَلَى الْأَرَائِكِ يَنْظُرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5884,'هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوا يَفْعَلُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5885,'إِذَا السَّمَاءُ انْشَقَّتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5886,'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5887,'وَإِذَا الْأَرْضُ مُدَّتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5888,'وَأَلْقَتْ مَا فِيهَا وَتَخَلَّتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5889,'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5890,'يَا أَيُّهَا الْإِنْسَانُ إِنَّكَ كَادِحٌ إِلَى رَبِّكَ كَدْحاً فَمُلَاقِيهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5891,'فَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5892,'فَسَوْفَ يُحَاسَبُ حِسَاباً يَسِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5893,'وَيَنْقَلِبُ إِلَى أَهْلِهِ مَسْرُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5894,'وَأَمَّا مَنْ أُوتِيَ كِتَابَهُ وَرَاءَ ظَهْرِهِ');
INSERT INTO "Quran"(id,imlai) VALUES(5895,'فَسَوْفَ يَدْعُو ثُبُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5896,'وَيَصْلَى سَعِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5897,'إِنَّهُ كَانَ فِي أَهْلِهِ مَسْرُوراً');
INSERT INTO "Quran"(id,imlai) VALUES(5898,'إِنَّهُ ظَنَّ أَنْ لَنْ يَحُورَ');
INSERT INTO "Quran"(id,imlai) VALUES(5899,'بَلَى إِنَّ رَبَّهُ كَانَ بِهِ بَصِيراً');
INSERT INTO "Quran"(id,imlai) VALUES(5900,'فَلَا أُقْسِمُ بِالشَّفَقِ');
INSERT INTO "Quran"(id,imlai) VALUES(5901,'وَاللَّيْلِ وَمَا وَسَقَ');
INSERT INTO "Quran"(id,imlai) VALUES(5902,'وَالْقَمَرِ إِذَا اتَّسَقَ');
INSERT INTO "Quran"(id,imlai) VALUES(5903,'لَتَرْكَبُنَّ طَبَقاً عَنْ طَبَقٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5904,'فَمَا لَهُمْ لَا يُؤْمِنُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5905,'وَإِذَا قُرِئَ عَلَيْهِمُ الْقُرْآَنُ لَا يَسْجُدُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5906,'بَلِ الَّذِينَ كَفَرُوا يُكَذِّبُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5907,'وَاللَّهُ أَعْلَمُ بِمَا يُوعُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(5908,'فَبَشِّرْهُمْ بِعَذَابٍ أَلِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5909,'إِلَّا الَّذِينَ آَمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5910,'وَالسَّمَاءِ ذَاتِ الْبُرُوجِ');
INSERT INTO "Quran"(id,imlai) VALUES(5911,'وَالْيَوْمِ الْمَوْعُودِ');
INSERT INTO "Quran"(id,imlai) VALUES(5912,'وَشَاهِدٍ وَمَشْهُودٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5913,'قُتِلَ أَصْحَابُ الْأُخْدُودِ');
INSERT INTO "Quran"(id,imlai) VALUES(5914,'النَّارِ ذَاتِ الْوَقُودِ');
INSERT INTO "Quran"(id,imlai) VALUES(5915,'إِذْ هُمْ عَلَيْهَا قُعُودٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5916,'وَهُمْ عَلَى مَا يَفْعَلُونَ بِالْمُؤْمِنِينَ شُهُودٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5917,'وَمَا نَقَمُوا مِنْهُمْ إِلَّا أَنْ يُؤْمِنُوا بِاللَّهِ الْعَزِيزِ الْحَمِيدِ');
INSERT INTO "Quran"(id,imlai) VALUES(5918,'الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَاللَّهُ عَلَى كُلِّ شَيْءٍ شَهِيدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5919,'إِنَّ الَّذِينَ فَتَنُوا الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ ثُمَّ لَمْ يَتُوبُوا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَرِيقِ');
INSERT INTO "Quran"(id,imlai) VALUES(5920,'إِنَّ الَّذِينَ آَمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ذَلِكَ الْفَوْزُ الْكَبِيرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5921,'إِنَّ بَطْشَ رَبِّكَ لَشَدِيدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5922,'إِنَّهُ هُوَ يُبْدِئُ وَيُعِيدُ');
INSERT INTO "Quran"(id,imlai) VALUES(5923,'وَهُوَ الْغَفُورُ الْوَدُودُ');
INSERT INTO "Quran"(id,imlai) VALUES(5924,'ذُو الْعَرْشِ الْمَجِيدُ');
INSERT INTO "Quran"(id,imlai) VALUES(5925,'فَعَّالٌ لِمَا يُرِيدُ');
INSERT INTO "Quran"(id,imlai) VALUES(5926,'هَلْ أَتَاكَ حَدِيثُ الْجُنُودِ');
INSERT INTO "Quran"(id,imlai) VALUES(5927,'فِرْعَوْنَ وَثَمُودَ');
INSERT INTO "Quran"(id,imlai) VALUES(5928,'بَلِ الَّذِينَ كَفَرُوا فِي تَكْذِيبٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5929,'وَاللَّهُ مِنْ وَرَائِهِمْ مُحِيطٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5930,'بَلْ هُوَ قُرْآَنٌ مَجِيدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5931,'فِي لَوْحٍ مَحْفُوظٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5932,'وَالسَّمَاءِ وَالطَّارِقِ');
INSERT INTO "Quran"(id,imlai) VALUES(5933,'وَمَا أَدْرَاكَ مَا الطَّارِقُ');
INSERT INTO "Quran"(id,imlai) VALUES(5934,'النَّجْمُ الثَّاقِبُ');
INSERT INTO "Quran"(id,imlai) VALUES(5935,'إِنْ كُلُّ نَفْسٍ لَمَّا عَلَيْهَا حَافِظٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5936,'فَلْيَنْظُرِ الْإِنْسَانُ مِمَّ خُلِقَ');
INSERT INTO "Quran"(id,imlai) VALUES(5937,'خُلِقَ مِنْ مَاءٍ دَافِقٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5938,'يَخْرُجُ مِنْ بَيْنِ الصُّلْبِ وَالتَّرَائِبِ');
INSERT INTO "Quran"(id,imlai) VALUES(5939,'إِنَّهُ عَلَى رَجْعِهِ لَقَادِرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5940,'يَوْمَ تُبْلَى السَّرَائِرُ');
INSERT INTO "Quran"(id,imlai) VALUES(5941,'فَمَا لَهُ مِنْ قُوَّةٍ وَلَا نَاصِرٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5942,'وَالسَّمَاءِ ذَاتِ الرَّجْعِ');
INSERT INTO "Quran"(id,imlai) VALUES(5943,'وَالْأَرْضِ ذَاتِ الصَّدْعِ');
INSERT INTO "Quran"(id,imlai) VALUES(5944,'إِنَّهُ لَقَوْلٌ فَصْلٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5945,'وَمَا هُوَ بِالْهَزْلِ');
INSERT INTO "Quran"(id,imlai) VALUES(5946,'إِنَّهُمْ يَكِيدُونَ كَيْداً');
INSERT INTO "Quran"(id,imlai) VALUES(5947,'وَأَكِيدُ كَيْداً');
INSERT INTO "Quran"(id,imlai) VALUES(5948,'فَمَهِّلِ الْكَافِرِينَ أَمْهِلْهُمْ رُوَيْداً');
INSERT INTO "Quran"(id,imlai) VALUES(5949,'سَبِّحِ اسْمَ رَبِّكَ الْأَعْلَى');
INSERT INTO "Quran"(id,imlai) VALUES(5950,'الَّذِي خَلَقَ فَسَوَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5951,'وَالَّذِي قَدَّرَ فَهَدَى');
INSERT INTO "Quran"(id,imlai) VALUES(5952,'وَالَّذِي أَخْرَجَ الْمَرْعَى');
INSERT INTO "Quran"(id,imlai) VALUES(5953,'فَجَعَلَهُ غُثَاءً أَحْوَى');
INSERT INTO "Quran"(id,imlai) VALUES(5954,'سَنُقْرِئُكَ فَلَا تَنْسَى');
INSERT INTO "Quran"(id,imlai) VALUES(5955,'إِلَّا مَا شَاءَ اللَّهُ إِنَّهُ يَعْلَمُ الْجَهْرَ وَمَا يَخْفَى');
INSERT INTO "Quran"(id,imlai) VALUES(5956,'وَنُيَسِّرُكَ لِلْيُسْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(5957,'فَذَكِّرْ إِنْ نَفَعَتِ الذِّكْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(5958,'سَيَذَّكَّرُ مَنْ يَخْشَى');
INSERT INTO "Quran"(id,imlai) VALUES(5959,'وَيَتَجَنَّبُهَا الْأَشْقَى');
INSERT INTO "Quran"(id,imlai) VALUES(5960,'الَّذِي يَصْلَى النَّارَ الْكُبْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(5961,'ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحْيَى');
INSERT INTO "Quran"(id,imlai) VALUES(5962,'قَدْ أَفْلَحَ مَنْ تَزَكَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5963,'وَذَكَرَ اسْمَ رَبِّهِ فَصَلَّى');
INSERT INTO "Quran"(id,imlai) VALUES(5964,'بَلْ تُؤْثِرُونَ الْحَيَاةَ الدُّنْيَا');
INSERT INTO "Quran"(id,imlai) VALUES(5965,'وَالْآَخِرَةُ خَيْرٌ وَأَبْقَى');
INSERT INTO "Quran"(id,imlai) VALUES(5966,'إِنَّ هَذَا لَفِي الصُّحُفِ الْأُولَى');
INSERT INTO "Quran"(id,imlai) VALUES(5967,'صُحُفِ إِبْرَاهِيمَ وَمُوسَى');
INSERT INTO "Quran"(id,imlai) VALUES(5968,'هَلْ أَتَاكَ حَدِيثُ الْغَاشِيَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(5969,'وُجُوهٌ يَوْمَئِذٍ خَاشِعَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5970,'عَامِلَةٌ نَاصِبَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5971,'تَصْلَى نَاراً حَامِيَةً');
INSERT INTO "Quran"(id,imlai) VALUES(5972,'تُسْقَى مِنْ عَيْنٍ آَنِيَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5973,'لَيْسَ لَهُمْ طَعَامٌ إِلَّا مِنْ ضَرِيعٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5974,'لَا يُسْمِنُ وَلَا يُغْنِي مِنْ جُوعٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5975,'وُجُوهٌ يَوْمَئِذٍ نَاعِمَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5976,'لِسَعْيِهَا رَاضِيَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5977,'فِي جَنَّةٍ عَالِيَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5978,'لَا تَسْمَعُ فِيهَا لَاغِيَةً');
INSERT INTO "Quran"(id,imlai) VALUES(5979,'فِيهَا عَيْنٌ جَارِيَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5980,'فِيهَا سُرُرٌ مَرْفُوعَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5981,'وَأَكْوَابٌ مَوْضُوعَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5982,'وَنَمَارِقُ مَصْفُوفَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5983,'وَزَرَابِيُّ مَبْثُوثَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5984,'أَفَلَا يَنْظُرُونَ إِلَى الْإِبِلِ كَيْفَ خُلِقَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5985,'وَإِلَى السَّمَاءِ كَيْفَ رُفِعَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5986,'وَإِلَى الْجِبَالِ كَيْفَ نُصِبَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5987,'وَإِلَى الْأَرْضِ كَيْفَ سُطِحَتْ');
INSERT INTO "Quran"(id,imlai) VALUES(5988,'فَذَكِّرْ إِنَّمَا أَنْتَ مُذَكِّرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(5989,'لَسْتَ عَلَيْهِمْ بِمُسَيْطِرٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5990,'إِلَّا مَنْ تَوَلَّى وَكَفَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5991,'فَيُعَذِّبُهُ اللَّهُ الْعَذَابَ الْأَكْبَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(5992,'إِنَّ إِلَيْنَا إِيَابَهُمْ');
INSERT INTO "Quran"(id,imlai) VALUES(5993,'ثُمَّ إِنَّ عَلَيْنَا حِسَابَهُمْ');
INSERT INTO "Quran"(id,imlai) VALUES(5994,'وَالْفَجْرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5995,'وَلَيَالٍ عَشْرٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5996,'وَالشَّفْعِ وَالْوَتْرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5997,'وَاللَّيْلِ إِذَا يَسْرِ');
INSERT INTO "Quran"(id,imlai) VALUES(5998,'هَلْ فِي ذَلِكَ قَسَمٌ لِذِي حِجْرٍ');
INSERT INTO "Quran"(id,imlai) VALUES(5999,'أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6000,'إِرَمَ ذَاتِ الْعِمَادِ');
INSERT INTO "Quran"(id,imlai) VALUES(6001,'الَّتِي لَمْ يُخْلَقْ مِثْلُهَا فِي الْبِلَادِ');
INSERT INTO "Quran"(id,imlai) VALUES(6002,'وَثَمُودَ الَّذِينَ جَابُوا الصَّخْرَ بِالْوَادِ');
INSERT INTO "Quran"(id,imlai) VALUES(6003,'وَفِرْعَوْنَ ذِي الْأَوْتَادِ');
INSERT INTO "Quran"(id,imlai) VALUES(6004,'الَّذِينَ طَغَوْا فِي الْبِلَادِ');
INSERT INTO "Quran"(id,imlai) VALUES(6005,'فَأَكْثَرُوا فِيهَا الْفَسَادَ');
INSERT INTO "Quran"(id,imlai) VALUES(6006,'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6007,'إِنَّ رَبَّكَ لَبِالْمِرْصَادِ');
INSERT INTO "Quran"(id,imlai) VALUES(6008,'فَأَمَّا الْإِنْسَانُ إِذَا مَا ابْتَلَاهُ رَبُّهُ فَأَكْرَمَهُ وَنَعَّمَهُ فَيَقُولُ رَبِّي أَكْرَمَنِ');
INSERT INTO "Quran"(id,imlai) VALUES(6009,'وَأَمَّا إِذَا مَا ابْتَلَاهُ فَقَدَرَ عَلَيْهِ رِزْقَهُ فَيَقُولُ رَبِّي أَهَانَنِ');
INSERT INTO "Quran"(id,imlai) VALUES(6010,'كَلَّا بَلْ لَا تُكْرِمُونَ الْيَتِيمَ');
INSERT INTO "Quran"(id,imlai) VALUES(6011,'وَلَا تَحَاضُّونَ عَلَى طَعَامِ الْمِسْكِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(6012,'وَتَأْكُلُونَ التُّرَاثَ أَكْلاً لَمّاً');
INSERT INTO "Quran"(id,imlai) VALUES(6013,'وَتُحِبُّونَ الْمَالَ حُبّاً جَمّاً');
INSERT INTO "Quran"(id,imlai) VALUES(6014,'كَلَّا إِذَا دُكَّتِ الْأَرْضُ دَكّاً دَكّاً');
INSERT INTO "Quran"(id,imlai) VALUES(6015,'وَجَاءَ رَبُّكَ وَالْمَلَكُ صَفّاً صَفّاً');
INSERT INTO "Quran"(id,imlai) VALUES(6016,'وَجِيءَ يَوْمَئِذٍ بِجَهَنَّمَ يَوْمَئِذٍ يَتَذَكَّرُ الْإِنْسَانُ وَأَنَّى لَهُ الذِّكْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(6017,'يَقُولُ يَا لَيْتَنِي قَدَّمْتُ لِحَيَاتِي');
INSERT INTO "Quran"(id,imlai) VALUES(6018,'فَيَوْمَئِذٍ لَا يُعَذِّبُ عَذَابَهُ أَحَدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6019,'وَلَا يُوثِقُ وَثَاقَهُ أَحَدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6020,'يَا أَيَّتُهَا النَّفْسُ الْمُطْمَئِنَّةُ');
INSERT INTO "Quran"(id,imlai) VALUES(6021,'ارْجِعِي إِلَى رَبِّكِ رَاضِيَةً مَرْضِيَّةً');
INSERT INTO "Quran"(id,imlai) VALUES(6022,'فَادْخُلِي فِي عِبَادِي');
INSERT INTO "Quran"(id,imlai) VALUES(6023,'وَادْخُلِي جَنَّتِي');
INSERT INTO "Quran"(id,imlai) VALUES(6024,'لَا أُقْسِمُ بِهَذَا الْبَلَدِ');
INSERT INTO "Quran"(id,imlai) VALUES(6025,'وَأَنْتَ حِلٌّ بِهَذَا الْبَلَدِ');
INSERT INTO "Quran"(id,imlai) VALUES(6026,'وَوَالِدٍ وَمَا وَلَدَ');
INSERT INTO "Quran"(id,imlai) VALUES(6027,'لَقَدْ خَلَقْنَا الْإِنْسَانَ فِي كَبَدٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6028,'أَيَحْسَبُ أَنْ لَنْ يَقْدِرَ عَلَيْهِ أَحَدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6029,'يَقُولُ أَهْلَكْتُ مَالاً لُبَداً');
INSERT INTO "Quran"(id,imlai) VALUES(6030,'أَيَحْسَبُ أَنْ لَمْ يَرَهُ أَحَدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6031,'أَلَمْ نَجْعَلْ لَهُ عَيْنَيْنِ');
INSERT INTO "Quran"(id,imlai) VALUES(6032,'وَلِسَاناً وَشَفَتَيْنِ');
INSERT INTO "Quran"(id,imlai) VALUES(6033,'وَهَدَيْنَاهُ النَّجْدَيْنِ');
INSERT INTO "Quran"(id,imlai) VALUES(6034,'فَلَا اقْتَحَمَ الْعَقَبَةَ');
INSERT INTO "Quran"(id,imlai) VALUES(6035,'وَمَا أَدْرَاكَ مَا الْعَقَبَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(6036,'فَكُّ رَقَبَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6037,'أَوْ إِطْعَامٌ فِي يَوْمٍ ذِي مَسْغَبَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6038,'يَتِيماً ذَا مَقْرَبَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6039,'أَوْ مِسْكِيناً ذَا مَتْرَبَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6040,'ثُمَّ كَانَ مِنَ الَّذِينَ آَمَنُوا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(6041,'أُولَئِكَ أَصْحَابُ الْمَيْمَنَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(6042,'وَالَّذِينَ كَفَرُوا بِآَيَاتِنَا هُمْ أَصْحَابُ الْمَشْأَمَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(6043,'عَلَيْهِمْ نَارٌ مُؤْصَدَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6044,'وَالشَّمْسِ وَضُحَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6045,'وَالْقَمَرِ إِذَا تَلَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6046,'وَالنَّهَارِ إِذَا جَلَّاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6047,'وَاللَّيْلِ إِذَا يَغْشَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6048,'وَالسَّمَاءِ وَمَا بَنَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6049,'وَالْأَرْضِ وَمَا طَحَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6050,'وَنَفْسٍ وَمَا سَوَّاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6051,'فَأَلْهَمَهَا فُجُورَهَا وَتَقْوَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6052,'قَدْ أَفْلَحَ مَنْ زَكَّاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6053,'وَقَدْ خَابَ مَنْ دَسَّاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6054,'كَذَّبَتْ ثَمُودُ بِطَغْوَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6055,'إِذِ انْبَعَثَ أَشْقَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6056,'فَقَالَ لَهُمْ رَسُولُ اللَّهِ نَاقَةَ اللَّهِ وَسُقْيَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6057,'فَكَذَّبُوهُ فَعَقَرُوهَا فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْبِهِمْ فَسَوَّاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6058,'وَلَا يَخَافُ عُقْبَاهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6059,'وَاللَّيْلِ إِذَا يَغْشَى');
INSERT INTO "Quran"(id,imlai) VALUES(6060,'وَالنَّهَارِ إِذَا تَجَلَّى');
INSERT INTO "Quran"(id,imlai) VALUES(6061,'وَمَا خَلَقَ الذَّكَرَ وَالْأُنْثَى');
INSERT INTO "Quran"(id,imlai) VALUES(6062,'إِنَّ سَعْيَكُمْ لَشَتَّى');
INSERT INTO "Quran"(id,imlai) VALUES(6063,'فَأَمَّا مَنْ أَعْطَى وَاتَّقَى');
INSERT INTO "Quran"(id,imlai) VALUES(6064,'وَصَدَّقَ بِالْحُسْنَى');
INSERT INTO "Quran"(id,imlai) VALUES(6065,'فَسَنُيَسِّرُهُ لِلْيُسْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(6066,'وَأَمَّا مَنْ بَخِلَ وَاسْتَغْنَى');
INSERT INTO "Quran"(id,imlai) VALUES(6067,'وَكَذَّبَ بِالْحُسْنَى');
INSERT INTO "Quran"(id,imlai) VALUES(6068,'فَسَنُيَسِّرُهُ لِلْعُسْرَى');
INSERT INTO "Quran"(id,imlai) VALUES(6069,'وَمَا يُغْنِي عَنْهُ مَالُهُ إِذَا تَرَدَّى');
INSERT INTO "Quran"(id,imlai) VALUES(6070,'إِنَّ عَلَيْنَا لَلْهُدَى');
INSERT INTO "Quran"(id,imlai) VALUES(6071,'وَإِنَّ لَنَا لَلْآَخِرَةَ وَالْأُولَى');
INSERT INTO "Quran"(id,imlai) VALUES(6072,'فَأَنْذَرْتُكُمْ نَاراً تَلَظَّى');
INSERT INTO "Quran"(id,imlai) VALUES(6073,'لَا يَصْلَاهَا إِلَّا الْأَشْقَى');
INSERT INTO "Quran"(id,imlai) VALUES(6074,'الَّذِي كَذَّبَ وَتَوَلَّى');
INSERT INTO "Quran"(id,imlai) VALUES(6075,'وَسَيُجَنَّبُهَا الْأَتْقَى');
INSERT INTO "Quran"(id,imlai) VALUES(6076,'الَّذِي يُؤْتِي مَالَهُ يَتَزَكَّى');
INSERT INTO "Quran"(id,imlai) VALUES(6077,'وَمَا لِأَحَدٍ عِنْدَهُ مِنْ نِعْمَةٍ تُجْزَى');
INSERT INTO "Quran"(id,imlai) VALUES(6078,'إِلَّا ابْتِغَاءَ وَجْهِ رَبِّهِ الْأَعْلَى');
INSERT INTO "Quran"(id,imlai) VALUES(6079,'وَلَسَوْفَ يَرْضَى');
INSERT INTO "Quran"(id,imlai) VALUES(6080,'وَالضُّحَى');
INSERT INTO "Quran"(id,imlai) VALUES(6081,'وَاللَّيْلِ إِذَا سَجَى');
INSERT INTO "Quran"(id,imlai) VALUES(6082,'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَى');
INSERT INTO "Quran"(id,imlai) VALUES(6083,'وَلَلْآَخِرَةُ خَيْرٌ لَكَ مِنَ الْأُولَى');
INSERT INTO "Quran"(id,imlai) VALUES(6084,'وَلَسَوْفَ يُعْطِيكَ رَبُّكَ فَتَرْضَى');
INSERT INTO "Quran"(id,imlai) VALUES(6085,'أَلَمْ يَجِدْكَ يَتِيماً فَآَوَى');
INSERT INTO "Quran"(id,imlai) VALUES(6086,'وَوَجَدَكَ ضَالّاً فَهَدَى');
INSERT INTO "Quran"(id,imlai) VALUES(6087,'وَوَجَدَكَ عَائِلاً فَأَغْنَى');
INSERT INTO "Quran"(id,imlai) VALUES(6088,'فَأَمَّا الْيَتِيمَ فَلَا تَقْهَرْ');
INSERT INTO "Quran"(id,imlai) VALUES(6089,'وَأَمَّا السَّائِلَ فَلَا تَنْهَرْ');
INSERT INTO "Quran"(id,imlai) VALUES(6090,'وَأَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ');
INSERT INTO "Quran"(id,imlai) VALUES(6091,'أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ');
INSERT INTO "Quran"(id,imlai) VALUES(6092,'وَوَضَعْنَا عَنْكَ وِزْرَكَ');
INSERT INTO "Quran"(id,imlai) VALUES(6093,'الَّذِي أَنْقَضَ ظَهْرَكَ');
INSERT INTO "Quran"(id,imlai) VALUES(6094,'وَرَفَعْنَا لَكَ ذِكْرَكَ');
INSERT INTO "Quran"(id,imlai) VALUES(6095,'فَإِنَّ مَعَ الْعُسْرِ يُسْراً');
INSERT INTO "Quran"(id,imlai) VALUES(6096,'إِنَّ مَعَ الْعُسْرِ يُسْراً');
INSERT INTO "Quran"(id,imlai) VALUES(6097,'فَإِذَا فَرَغْتَ فَانْصَبْ');
INSERT INTO "Quran"(id,imlai) VALUES(6098,'وَإِلَى رَبِّكَ فَارْغَبْ');
INSERT INTO "Quran"(id,imlai) VALUES(6099,'وَالتِّينِ وَالزَّيْتُونِ');
INSERT INTO "Quran"(id,imlai) VALUES(6100,'وَطُورِ سِينِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(6101,'وَهَذَا الْبَلَدِ الْأَمِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(6102,'لَقَدْ خَلَقْنَا الْإِنْسَانَ فِي أَحْسَنِ تَقْوِيمٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6103,'ثُمَّ رَدَدْنَاهُ أَسْفَلَ سَافِلِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(6104,'إِلَّا الَّذِينَ آَمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6105,'فَمَا يُكَذِّبُكَ بَعْدُ بِالدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(6106,'أَلَيْسَ اللَّهُ بِأَحْكَمِ الْحَاكِمِينَ');
INSERT INTO "Quran"(id,imlai) VALUES(6107,'اقْرَأْ بِاسْمِ رَبِّكَ الَّذِي خَلَقَ');
INSERT INTO "Quran"(id,imlai) VALUES(6108,'خَلَقَ الْإِنْسَانَ مِنْ عَلَقٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6109,'اقْرَأْ وَرَبُّكَ الْأَكْرَمُ');
INSERT INTO "Quran"(id,imlai) VALUES(6110,'الَّذِي عَلَّمَ بِالْقَلَمِ');
INSERT INTO "Quran"(id,imlai) VALUES(6111,'عَلَّمَ الْإِنْسَانَ مَا لَمْ يَعْلَمْ');
INSERT INTO "Quran"(id,imlai) VALUES(6112,'كَلَّا إِنَّ الْإِنْسَانَ لَيَطْغَى');
INSERT INTO "Quran"(id,imlai) VALUES(6113,'أَنْ رَآَهُ اسْتَغْنَى');
INSERT INTO "Quran"(id,imlai) VALUES(6114,'إِنَّ إِلَى رَبِّكَ الرُّجْعَى');
INSERT INTO "Quran"(id,imlai) VALUES(6115,'أَرَأَيْتَ الَّذِي يَنْهَى');
INSERT INTO "Quran"(id,imlai) VALUES(6116,'عَبْداً إِذَا صَلَّى');
INSERT INTO "Quran"(id,imlai) VALUES(6117,'أَرَأَيْتَ إِنْ كَانَ عَلَى الْهُدَى');
INSERT INTO "Quran"(id,imlai) VALUES(6118,'أَوْ أَمَرَ بِالتَّقْوَى');
INSERT INTO "Quran"(id,imlai) VALUES(6119,'أَرَأَيْتَ إِنْ كَذَّبَ وَتَوَلَّى');
INSERT INTO "Quran"(id,imlai) VALUES(6120,'أَلَمْ يَعْلَمْ بِأَنَّ اللَّهَ يَرَى');
INSERT INTO "Quran"(id,imlai) VALUES(6121,'كَلَّا لَئِنْ لَمْ يَنْتَهِ لَنَسْفَعَنْ بِالنَّاصِيَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(6122,'نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6123,'فَلْيَدْعُ نَادِيَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(6124,'سَنَدْعُ الزَّبَانِيَةَ');
INSERT INTO "Quran"(id,imlai) VALUES(6125,'كَلَّا لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ');
INSERT INTO "Quran"(id,imlai) VALUES(6126,'إِنَّا أَنْزَلْنَاهُ فِي لَيْلَةِ الْقَدْرِ');
INSERT INTO "Quran"(id,imlai) VALUES(6127,'وَمَا أَدْرَاكَ مَا لَيْلَةُ الْقَدْرِ');
INSERT INTO "Quran"(id,imlai) VALUES(6128,'لَيْلَةُ الْقَدْرِ خَيْرٌ مِنْ أَلْفِ شَهْرٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6129,'تَنَزَّلُ الْمَلَائِكَةُ وَالرُّوحُ فِيهَا بِإِذْنِ رَبِّهِمْ مِنْ كُلِّ أَمْرٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6130,'سَلَامٌ هِيَ حَتَّى مَطْلَعِ الْفَجْرِ');
INSERT INTO "Quran"(id,imlai) VALUES(6131,'لَمْ يَكُنِ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَالْمُشْرِكِينَ مُنْفَكِّينَ حَتَّى تَأْتِيَهُمُ الْبَيِّنَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(6132,'رَسُولٌ مِنَ اللَّهِ يَتْلُو صُحُفاً مُطَهَّرَةً');
INSERT INTO "Quran"(id,imlai) VALUES(6133,'فِيهَا كُتُبٌ قَيِّمَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6134,'وَمَا تَفَرَّقَ الَّذِينَ أُوتُوا الْكِتَابَ إِلَّا مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(6135,'وَمَا أُمِرُوا إِلَّا لِيَعْبُدُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ حُنَفَاءَ وَيُقِيمُوا الصَّلَاةَ وَيُؤْتُوا الزَّكَاةَ وَذَلِكَ دِينُ الْقَيِّمَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(6136,'إِنَّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَالْمُشْرِكِينَ فِي نَارِ جَهَنَّمَ خَالِدِينَ فِيهَا أُولَئِكَ هُمْ شَرُّ الْبَرِيَّةِ');
INSERT INTO "Quran"(id,imlai) VALUES(6137,'إِنَّ الَّذِينَ آَمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَئِكَ هُمْ خَيْرُ الْبَرِيَّةِ');
INSERT INTO "Quran"(id,imlai) VALUES(6138,'جَزَاؤُهُمْ عِنْدَ رَبِّهِمْ جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَداً رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ذَلِكَ لِمَنْ خَشِيَ رَبَّهُ');
INSERT INTO "Quran"(id,imlai) VALUES(6139,'إِذَا زُلْزِلَتِ الْأَرْضُ زِلْزَالَهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6140,'وَأَخْرَجَتِ الْأَرْضُ أَثْقَالَهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6141,'وَقَالَ الْإِنْسَانُ مَا لَهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6142,'يَوْمَئِذٍ تُحَدِّثُ أَخْبَارَهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6143,'بِأَنَّ رَبَّكَ أَوْحَى لَهَا');
INSERT INTO "Quran"(id,imlai) VALUES(6144,'يَوْمَئِذٍ يَصْدُرُ النَّاسُ أَشْتَاتاً لِيُرَوْا أَعْمَالَهُمْ');
INSERT INTO "Quran"(id,imlai) VALUES(6145,'فَمَنْ يَعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْراً يَرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(6146,'وَمَنْ يَعْمَلْ مِثْقَالَ ذَرَّةٍ شَرّاً يَرَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(6147,'وَالْعَادِيَاتِ ضَبْحاً');
INSERT INTO "Quran"(id,imlai) VALUES(6148,'فَالْمُورِيَاتِ قَدْحاً');
INSERT INTO "Quran"(id,imlai) VALUES(6149,'فَالْمُغِيرَاتِ صُبْحاً');
INSERT INTO "Quran"(id,imlai) VALUES(6150,'فَأَثَرْنَ بِهِ نَقْعاً');
INSERT INTO "Quran"(id,imlai) VALUES(6151,'فَوَسَطْنَ بِهِ جَمْعاً');
INSERT INTO "Quran"(id,imlai) VALUES(6152,'إِنَّ الْإِنْسَانَ لِرَبِّهِ لَكَنُودٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6153,'وَإِنَّهُ عَلَى ذَلِكَ لَشَهِيدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6154,'وَإِنَّهُ لِحُبِّ الْخَيْرِ لَشَدِيدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6155,'أَفَلَا يَعْلَمُ إِذَا بُعْثِرَ مَا فِي الْقُبُورِ');
INSERT INTO "Quran"(id,imlai) VALUES(6156,'وَحُصِّلَ مَا فِي الصُّدُورِ');
INSERT INTO "Quran"(id,imlai) VALUES(6157,'إِنَّ رَبَّهُمْ بِهِمْ يَوْمَئِذٍ لَخَبِيرٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6158,'الْقَارِعَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(6159,'مَا الْقَارِعَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(6160,'وَمَا أَدْرَاكَ مَا الْقَارِعَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(6161,'يَوْمَ يَكُونُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوثِ');
INSERT INTO "Quran"(id,imlai) VALUES(6162,'وَتَكُونُ الْجِبَالُ كَالْعِهْنِ الْمَنْفُوشِ');
INSERT INTO "Quran"(id,imlai) VALUES(6163,'فَأَمَّا مَنْ ثَقُلَتْ مَوَازِينُهُ');
INSERT INTO "Quran"(id,imlai) VALUES(6164,'فَهُوَ فِي عِيشَةٍ رَاضِيَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6165,'وَأَمَّا مَنْ خَفَّتْ مَوَازِينُهُ');
INSERT INTO "Quran"(id,imlai) VALUES(6166,'فَأُمُّهُ هَاوِيَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6167,'وَمَا أَدْرَاكَ مَا هِيَهْ');
INSERT INTO "Quran"(id,imlai) VALUES(6168,'نَارٌ حَامِيَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6169,'أَلْهَاكُمُ التَّكَاثُرُ');
INSERT INTO "Quran"(id,imlai) VALUES(6170,'حَتَّى زُرْتُمُ الْمَقَابِرَ');
INSERT INTO "Quran"(id,imlai) VALUES(6171,'كَلَّا سَوْفَ تَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(6172,'ثُمَّ كَلَّا سَوْفَ تَعْلَمُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(6173,'كَلَّا لَوْ تَعْلَمُونَ عِلْمَ الْيَقِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(6174,'لَتَرَوُنَّ الْجَحِيمَ');
INSERT INTO "Quran"(id,imlai) VALUES(6175,'ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَقِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(6176,'ثُمَّ لَتُسْأَلُنَّ يَوْمَئِذٍ عَنِ النَّعِيمِ');
INSERT INTO "Quran"(id,imlai) VALUES(6177,'وَالْعَصْرِ');
INSERT INTO "Quran"(id,imlai) VALUES(6178,'إِنَّ الْإِنْسَانَ لَفِي خُسْرٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6179,'إِلَّا الَّذِينَ آَمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَتَوَاصَوْا بِالْحَقِّ وَتَوَاصَوْا بِالصَّبْرِ');
INSERT INTO "Quran"(id,imlai) VALUES(6180,'وَيْلٌ لِكُلِّ هُمَزَةٍ لُمَزَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6181,'الَّذِي جَمَعَ مَالاً وَعَدَّدَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(6182,'يَحْسَبُ أَنَّ مَالَهُ أَخْلَدَهُ');
INSERT INTO "Quran"(id,imlai) VALUES(6183,'كَلَّا لَيُنْبَذَنَّ فِي الْحُطَمَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(6184,'وَمَا أَدْرَاكَ مَا الْحُطَمَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(6185,'نَارُ اللَّهِ الْمُوقَدَةُ');
INSERT INTO "Quran"(id,imlai) VALUES(6186,'الَّتِي تَطَّلِعُ عَلَى الْأَفْئِدَةِ');
INSERT INTO "Quran"(id,imlai) VALUES(6187,'إِنَّهَا عَلَيْهِمْ مُؤْصَدَةٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6188,'فِي عَمَدٍ مُمَدَّدَةٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6189,'أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحَابِ الْفِيلِ');
INSERT INTO "Quran"(id,imlai) VALUES(6190,'أَلَمْ يَجْعَلْ كَيْدَهُمْ فِي تَضْلِيلٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6191,'وَأَرْسَلَ عَلَيْهِمْ طَيْراً أَبَابِيلَ');
INSERT INTO "Quran"(id,imlai) VALUES(6192,'تَرْمِيهِمْ بِحِجَارَةٍ مِنْ سِجِّيلٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6193,'فَجَعَلَهُمْ كَعَصْفٍ مَأْكُولٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6194,'لِإِيلَافِ قُرَيْشٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6195,'إِيلَافِهِمْ رِحْلَةَ الشِّتَاءِ وَالصَّيْفِ');
INSERT INTO "Quran"(id,imlai) VALUES(6196,'فَلْيَعْبُدُوا رَبَّ هَذَا الْبَيْتِ');
INSERT INTO "Quran"(id,imlai) VALUES(6197,'الَّذِي أَطْعَمَهُمْ مِنْ جُوعٍ وَآَمَنَهُمْ مِنْ خَوْفٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6198,'أَرَأَيْتَ الَّذِي يُكَذِّبُ بِالدِّينِ');
INSERT INTO "Quran"(id,imlai) VALUES(6199,'فَذَلِكَ الَّذِي يَدُعُّ الْيَتِيمَ');
INSERT INTO "Quran"(id,imlai) VALUES(6200,'وَلَا يَحُضُّ عَلَى طَعَامِ الْمِسْكِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(6201,'فَوَيْلٌ لِلْمُصَلِّينَ');
INSERT INTO "Quran"(id,imlai) VALUES(6202,'الَّذِينَ هُمْ عَنْ صَلَاتِهِمْ سَاهُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(6203,'الَّذِينَ هُمْ يُرَاءُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(6204,'وَيَمْنَعُونَ الْمَاعُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(6205,'إِنَّا أَعْطَيْنَاكَ الْكَوْثَرَ');
INSERT INTO "Quran"(id,imlai) VALUES(6206,'فَصَلِّ لِرَبِّكَ وَانْحَرْ');
INSERT INTO "Quran"(id,imlai) VALUES(6207,'إِنَّ شَانِئَكَ هُوَ الْأَبْتَرُ');
INSERT INTO "Quran"(id,imlai) VALUES(6208,'قُلْ يَا أَيُّهَا الْكَافِرُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(6209,'لَا أَعْبُدُ مَا تَعْبُدُونَ');
INSERT INTO "Quran"(id,imlai) VALUES(6210,'وَلَا أَنْتُمْ عَابِدُونَ مَا أَعْبُدُ');
INSERT INTO "Quran"(id,imlai) VALUES(6211,'وَلَا أَنَا عَابِدٌ مَا عَبَدْتُمْ');
INSERT INTO "Quran"(id,imlai) VALUES(6212,'وَلَا أَنْتُمْ عَابِدُونَ مَا أَعْبُدُ');
INSERT INTO "Quran"(id,imlai) VALUES(6213,'لَكُمْ دِينُكُمْ وَلِيَ دِينِ');
INSERT INTO "Quran"(id,imlai) VALUES(6214,'إِذَا جَاءَ نَصْرُ اللَّهِ وَالْفَتْحُ');
INSERT INTO "Quran"(id,imlai) VALUES(6215,'وَرَأَيْتَ النَّاسَ يَدْخُلُونَ فِي دِينِ اللَّهِ أَفْوَاجاً');
INSERT INTO "Quran"(id,imlai) VALUES(6216,'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُ إِنَّهُ كَانَ تَوَّاباً');
INSERT INTO "Quran"(id,imlai) VALUES(6217,'تَبَّتْ يَدَا أَبِي لَهَبٍ وَتَبَّ');
INSERT INTO "Quran"(id,imlai) VALUES(6218,'مَا أَغْنَى عَنْهُ مَالُهُ وَمَا كَسَبَ');
INSERT INTO "Quran"(id,imlai) VALUES(6219,'سَيَصْلَى نَاراً ذَاتَ لَهَبٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6220,'وَامْرَأَتُهُ حَمَّالَةَ الْحَطَبِ');
INSERT INTO "Quran"(id,imlai) VALUES(6221,'فِي جِيدِهَا حَبْلٌ مِنْ مَسَدٍ');
INSERT INTO "Quran"(id,imlai) VALUES(6222,'قُلْ هُوَ اللَّهُ أَحَدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6223,'اللَّهُ الصَّمَدُ');
INSERT INTO "Quran"(id,imlai) VALUES(6224,'لَمْ يَلِدْ وَلَمْ يُولَدْ');
INSERT INTO "Quran"(id,imlai) VALUES(6225,'وَلَمْ يَكُنْ لَهُ كُفُواً أَحَدٌ');
INSERT INTO "Quran"(id,imlai) VALUES(6226,'قُلْ أَعُوذُ بِرَبِّ الْفَلَقِ');
INSERT INTO "Quran"(id,imlai) VALUES(6227,'مِنْ شَرِّ مَا خَلَقَ');
INSERT INTO "Quran"(id,imlai) VALUES(6228,'وَمِنْ شَرِّ غَاسِقٍ إِذَا وَقَبَ');
INSERT INTO "Quran"(id,imlai) VALUES(6229,'وَمِنْ شَرِّ النَّفَّاثَاتِ فِي الْعُقَدِ');
INSERT INTO "Quran"(id,imlai) VALUES(6230,'وَمِنْ شَرِّ حَاسِدٍ إِذَا حَسَدَ');
INSERT INTO "Quran"(id,imlai) VALUES(6231,'قُلْ أَعُوذُ بِرَبِّ النَّاسِ');
INSERT INTO "Quran"(id,imlai) VALUES(6232,'مَلِكِ النَّاسِ');
