PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE Quran (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, othmani TEXT, imlai TEXT);
CREATE TABLE Sajadat (sura INTEGER, aya INTEGER, sajda_type INTEGER, comment TEXT);
CREATE TABLE Tahzeeb (sura INTEGER, aya INTEGER);
CREATE TABLE SuraInfo (sura_name TEXT, other_names TEXT, makki INTEGER, starting_row, comment TEXT);
DELETE FROM sqlite_sequence;
CREATE INDEX Sajadat_Index on Sajadat (sura);
CREATE INDEX Tahzeeb_Index on Tahzeeb (sura);
COMMIT;
